/*-------------------------------------------------------------------------------------------------+
>>  최초 화면 Load時 처리 할 사항
+-------------------------------------------------------------------------------------------------*/
function fcFormLoadSetting(obj) {  


    //공통코드 조회
	fcd_GetComboDs(dsInComCd);



	fcd_GetTmsDs("dsTms", "00"+sMeetCd, "",sRaceDt);


	if(Length(sSeq) >0){
		btnEndReq.Enable = false;

		calRaceDt.Enable = false;
		calReqDt.Enable = false;
		taReqRsn.Enable = false;

		btnAddFile.Visible = false;

		fcSearch();


	}else{




		dsStayEndMana.SetColumn(0,"RACE_DT",sRaceDt);
		dsStayEndMana.SetColumn(0,"BRNC_CD",sBrncCd);
		dsStayEndMana.SetColumn(0,"BRNC_NM",sBrncNm);
		dsStayEndMana.SetColumn(0,"MEET_CD",sMeetCd);
		dsStayEndMana.SetColumn(0,"REQ_DT",TODAY());


		calReqDt.Enable = false;

		taReqRsn.SetFocus();
	}

	dsStayEndMana.SetColumn(0,"TMS",dsTms.GetColumn(0,"TMS_CD"));
}

