/*-------------------------------------------------------------------------------------------------+
>>  취소 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcInit(obj) {

	dsStayEndMana.ClearData();

}


/*-------------------------------------------------------------------------------------------------+
>>  조회 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcSearch(obj) {

	var sServiceName = "";
	var sInDataSet   = "";
	var sOutDataSet  = "";


	 sServiceName = "rem3011-service:searchStayEndMana";
	 sInDataSet   = "";
	 sOutDataSet  = "dsStayEndMana=dsStayEndMana ";

	 //조회조건 : 

	 fcd_AddParam("RACE_DT", sRaceDt);
	 fcd_AddParam("BRNC_CD", sBrncCd);
	 fcd_AddParam("MEET_CD", sMeetCd);
	 fcd_AddParam("SEQ", sSeq);


	 fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", false);
}


/*-------------------------------------------------------------------------------------------------+
>>  저장 하기전에 필수항목 체크
+-------------------------------------------------------------------------------------------------*/
function fcSaveCheck(obj) {
	// 변경된 자료가 있는지 체크
	if ( !fnc_DatasetChangeCheck(dsStayEndMana) ) {
		// 저장 할 자료가 없음
		fnc_Message(SNIS_COM_1006, "N");
		return false;
	}

	// 필수항목체크



	return true;
}

/*-------------------------------------------------------------------------------------------------+
>>  저장 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcSave(obj) {

	var sServiceName = "";
	var sInDataSet   = "";
	var sOutDataSet  = "";


	switch ( obj.id ) {
		case "btnEndReq":
			if ( !fcSaveCheck() ) return;


				//첨부파일 서버 업로드 

			if( dsUploadFile.GetRowCount() > 0 ) {
				for ( i = 0; i < dsUploadFile.GetRowCount(); i++ ) {
					if(dsUploadFile.getColumn(i, "CHK") == "1" ) {
						var rtn_arr = fnc_FileWrite(  HttpFile 
													, dsUploadFile.GetColumn(i, "FILE_PATH" )
													, dsUploadFile.GetColumn(i, "FILE_NM" ) 
													,"");

						var fileSize = HttpFile.GetFileSizeLocal(dsUploadFile.GetColumn(i, "FILE_PATH" ));

						if ( rtn_arr[0] != "SUCC" ) {
							alert(rtn_arr[1] + "파일 업로드 에러발생");
							return false;
						}

						//dsUploadFile.SetColumn(i, "SEQ_NO"      , dsOutNextSeqNo.getColumn(0,"NEXTSEQNO") );
						//dsUploadFile.SetColumn(i, "FILE_SEQ"    , toInteger(dsOutNextSeqNo.getColumn(0,"NEXTFILESEQNO")) + toInteger(i) );
						dsUploadFile.SetColumn(i, "FILE_PATH"    , rtn_arr[1] );
						dsUploadFile.SetColumn(i, "FILE_SIZE"   , fileSize );

					} // end if
				}
			} // end 



			sServiceName = "rem3011-service:saveStayEndMana";
			sInDataSet   = "dsStayEndMana=dsStayEndMana:u  dsUploadFile=dsUploadFile:u";

			sOutDataSet  = "dsStayEndMana=dsStayEndMana ";


			fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", false);

			break;


	}


}




/*-------------------------------------------------------------------------------------------------+
>>  화면 종료(닫기) 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcEnd(obj) {
	// 화면 종료
	Close();
}




/***************************************************************************************************
*                                      사용자 정의  처리 부분                                      *
***************************************************************************************************/
/*-------------------------------------------------------------------------------------------------+
>>  Transaction 후 처리 해야 할 내용!
+-------------------------------------------------------------------------------------------------*/
function fcCallBack(sServiceName, ErrorCode, ErrorMSG) {
	if ( !fnc_result(ErrorCode, ErrorMSG) ) return;


	// 회선   저장 완료 후
	if ( sServiceName == "rem3011-service:saveStayEndMana" ) {
		fcSendMngMsg();

		fnc_Message(SNIS_CRA_0004, "N","1");



		fcEnd();
	}


	if ( sServiceName == "rem3011-service:searchStayEndMana" ) {
			dsUploadFile.ClearData();

			fcSearchFile(dsStayEndMana.GetColumn(dsStayEndMana.lastrow,"ATT_FILE_KEY"));
	}




}


function fcBtnOnClick(obj)
{
	switch ( obj.id ) {
		case "btnAddFile":

			sResponse = fcd_GetSelectFile();	

			dsUploadFile.ClearData();

			row = dsUploadFile.addRow();


			if ( !fnc_IsNull(sResponse) ) {
				dsUploadFile.SetColumn(row, "CHK"       , "1"         );
				dsUploadFile.SetColumn(row, "FILE_PATH"  , sResponse[0] + "\\" + sResponse[1]);
				dsUploadFile.SetColumn(row, "FILE_NM" , sResponse[1]);


			}	


			break;


	}

}


/*-------------------------------------------------------------------------------------------------+
>>  첨부파일 조회 
+-------------------------------------------------------------------------------------------------*/
function fcSearchFile(sAttFileKey){

		var sServiceName = "";
		var sInDataSet   = "";
		var sOutDataSet  = "";


		 sServiceName = "rsy4011-service:searchFileMana";
		 sInDataSet   = "";
		 sOutDataSet  = "dsUploadFile=dsUploadFile ";

		 //조회조건 : 
		 fcd_AddParam("ATT_FILE_KEY", sAttFileKey);

		 fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", false);

}


function fcEdOnClick(obj)
{
	if(Length(sSeq) >0 && dsUploadFile.GetRowCount()>0){


			var sLocalFileName  = fcd_GetSelectFile("Save", dsUploadFile.GetColumn(0, "FILE_NM"));
			var sServerFileName = dsUploadFile.GetColumn(0, "FILE_PATH");
			var sFileSize       = dsUploadFile.GetColumn(0, "FILE_SIZE");
			if ( sLocalFileName == null ) return;

			fnc_FileRead(HttpFile, sLocalFileName[0] + "\\" + sLocalFileName[1], sServerFileName, sFileSize, 0, "PROG_BAR", dsUploadFile);

	}
}


function fcSendMngMsg(){


	  var sSendUserId = gdsUser.GetColumn(0,"USER_ID");
	  var sSendUserNm = gdsUser.GetColumn(0,"USER_NM");
	  var sSendUserHpNo = gdsUser.GetColumn(0,"HP_NO");
	  if (fnc_IsNull(sSendUserHpNo)) return;

	  var sRecvUser = "";
	  var sTitle	= "체류인원 마감취소요청";
	  var sDesc		= "요청사유 : " +dsStayEndMana.GetColumn(dsStayEndMana.row,"REQ_RSN");

	  fcd_SendMsgByAlarmDs("001", sSendUserId, sSendUserNm, sSendUserHpNo, sTitle, sDesc, "REM3070" );
}
