function fcSearchCheck(obj) {

	fcInit(obj);

	return true;
}



/*-------------------------------------------------------------------------------------------------+
>>  Á¶È¸ ¹öÆ° Å¬¸¯ ãÁ Ã³¸®
+-------------------------------------------------------------------------------------------------*/
function fcSearch(obj) {

	var sServiceName = "";
	var sInDataSet   = "";
	var sOutDataSet  = "";
	if ( !fcSearchCheck(obj) ) return;


	 sServiceName = "rem3030-service:searchStayStatByDay";
	 sInDataSet   = "";
	 sOutDataSet  = "dsStayStatByDay=dsStayStatByDay ";

	 //Á¶È¸Á¶°Ç : 

	 fcd_AddParam("MEET_CD", rdoMeetCd.Value);
	 fcd_AddParam("BRNC_CD", cboSearchBrncCd.Value);
	 fcd_AddParam("RACE_STR_DT", calRaceStrDt.Value);
	 fcd_AddParam("RACE_END_DT", calRaceEndDt.Value);
	 fcd_AddParam("STND_YEAR", sCurYear);
	 fcd_AddParam("STND_GBN", cboStndGbn.Value);

	 if(rdoMeetCd.Value == "001"){
		fcd_AddParam("MEET_BRNC_CD", "00");

	 }else if(rdoMeetCd.Value == "003"){
		fcd_AddParam("MEET_BRNC_CD", "98");

	 }



	 fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", true);
}



