function fcCboOnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{

	switch ( obj.id ) {
		case "cboSearchBrncCd":

			fcSearch();

			break;

	    case "cboStndGbn":

			fcSearch();

			break;

	}
}

function fcDsOnLoadCompleted(obj,nErrorCode,strErrorMsg,nReason)
{
	switch ( obj.id ) {
		case "dsMeetCd":

				rdoMeetCd.Value = "001";

			break;

		case "dsBrncCd":

			dsBrncCd.InsertRow(0);
			dsBrncCd.SetColumn(0, "CD_NM", "ÀüÃ¼");
			cboSearchBrncCd.index = 0;

			break;	

	}
}

function fcRdoOnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
	switch ( obj.id ) {
		case "rdoMeetCd":
			fcSetCellByMeetCd();
			fcSearch();
			break;

	}
}

function fcSetCellByMeetCd(){
	var sMeetCd = rdoMeetCd.Value;

	var sCellStr = ""; // 3:¿ù, 4:È­ ,5:¼ö,6:¸ñ,7:±Ý ,8:Åä, 9:ÀÏ
	var nCellWidth = 73;

	if(sMeetCd == "001"){
		sCellStr = "5,6,7,8,9,12,13,14,15,16";	
		nCellWidth = 59;
	}else if(sMeetCd == "003"){
		sCellStr = "4,5,6,9,11,12,13,16";		
		nCellWidth = 72;
	}

	// ÃÊ±âÈ­
	for(var i = 3; i <=16 ; i++){		
		grdRaceStatByDay.SetColProp(i,"WIDTH",0);
	}

	var aCell = split(sCellStr,",");

	for(var i =0; i <=aCell.length; i++){
		grdRaceStatByDay.SetColProp(aCell[i],"WIDTH",nCellWidth);
	}

}
