/*-------------------------------------------------------------------------------------------------+
>>  취소 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcInit(obj) {

	dsStayEndMana.ClearData();

}

/*-------------------------------------------------------------------------------------------------+
>>  조회 버튼 클릭 時 필수조건 체크 및 그리드 초기화
+-------------------------------------------------------------------------------------------------*/
function fcSearchCheck(obj) {

	fcInit(obj);

	return true;
}



/*-------------------------------------------------------------------------------------------------+
>>  조회 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcSearch(obj) {

	var sServiceName = "";
	var sInDataSet   = "";
	var sOutDataSet  = "";
	if ( !fcSearchCheck(obj) ) return;


	 sServiceName = "rem3070-service:searchStayEndMana";
	 sInDataSet   = "";
	 sOutDataSet  = "dsStayEndMana=dsStayEndMana ";

	 //조회조건 : 

	 fcd_AddParam("MEET_CD", rdoMeetCd.Value);
	 fcd_AddParam("BRNC_CD", cboSearchBrncCd.Value);
	 fcd_AddParam("RACE_STR_DT", calRaceStrDt.Value);
	 fcd_AddParam("RACE_END_DT", calRaceEndDt.Value);
	 fcd_AddParam("END_YN", cboEndYn.Value);


	 fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", true);
}



/*-------------------------------------------------------------------------------------------------+
>>  저장 하기전에 필수항목 체크
+-------------------------------------------------------------------------------------------------*/
function fcSaveCheck(obj) {
	// 변경된 자료가 있는지 체크
	if ( !fnc_DatasetChangeCheck(dsStayEndMana) ) {
		// 저장 할 자료가 없음
		fnc_Message(SNIS_COM_1006, "N");
		return false;
	}

	// 필수항목체크




	return true;
}


var svcId = "";

/*-------------------------------------------------------------------------------------------------+
>>  저장 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcSave(obj) {

	var sServiceName = "";
	var sInDataSet   = "";
	var sOutDataSet  = "";


	switch ( obj.id ) {
		case "btnSave":
			if ( !fcSaveCheck() ) return;

			sServiceName = "rem3070-service:saveStayEnd";
			sInDataSet   = "dsStayEndMana=dsStayEndMana:u ";

			sOutDataSet  = "dsStayEndMana=dsStayEndMana ";

			fcd_AddParam("JOB_GBN", "01");
			fcd_AddParam("STAY_END_STAT_CD", "002");

			fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", false);


			break;

		case "btnEndSave" :

			if ( !fcSaveCheck() ) return;

			sServiceName = "rem3070-service:saveStayEnd";
			sInDataSet   = "dsStayEndMana=dsStayEndMana:u ";


			sOutDataSet  = "dsStayEndMana=dsStayEndMana ";


			fcd_AddParam("JOB_GBN", "02");
			fcd_AddParam("STAY_END_STAT_CD", "002");

			svcId = "saveStayEndCancel";

			fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", false);


			break;


		case "btnRtnSave" :

			if ( !fcSaveCheck() ) return;

			sServiceName = "rem3070-service:saveStayEnd";
			sInDataSet   = "dsStayEndMana=dsStayEndMana:u ";


			sOutDataSet  = "dsStayEndMana=dsStayEndMana ";


			fcd_AddParam("JOB_GBN", "03");
			fcd_AddParam("STAY_END_STAT_CD", "003");

			svcId = "saveStayEndReturn";


			fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", false);


			break;	

	}


}


