/*-------------------------------------------------------------------------------------------------+
>>  Transaction 후 처리 해야 할 내용!
+-------------------------------------------------------------------------------------------------*/
function fcCallBack(sServiceName, ErrorCode, ErrorMSG) {
	if ( !fnc_result(ErrorCode, ErrorMSG) ) return;

	// 체류인원   조회 완료 후
	if ( sServiceName == "rem3070-service:searchStayEndMana" ) {


	}


	// 체류인원   저장 완료 후
	if ( sServiceName == "rem3070-service:saveStayEndMana" ) {

		if(svcId == "saveStayEndCancel" || svcId =="saveStayEndReturn"){
			fcSendMngMsg(svcId);

		}

		fcSearch();

	    svcId = "";
	}



}

function fcCboOnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{

	switch ( obj.id ) {
		case "cboSearchBrncCd":

			fcSearch();

			break;
		case "cboSearchMeetCd":

			fcSearch();

			break;	
		case "cboEndYn":

			fcSearch();

			break;	

	}
}

function fcDsOnLoadCompleted(obj,nErrorCode,strErrorMsg,nReason)
{
	switch ( obj.id ) {
		case "dsMeetCd":

				rdoMeetCd.Value = "001";

			break;

		case "dsBrncCd":

			dsBrncCd.InsertRow(0);
			dsBrncCd.SetColumn(0, "CD_NM", "전체");
			cboSearchBrncCd.index = 0;

			break;	

		case "dsEnd":

			dsEnd.InsertRow(0);
			dsEnd.SetColumn(0, "CD_NM", "전체");
			cboEndYn.index = 0;

			break;	

	}
}

function fcRdoOnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
	switch ( obj.id ) {
		case "rdoMeetCd":
			fcSearch();
			break;

	}
}

function fcGrdOnCellDBLClick(obj,nRow,nCell,nX,nY,nPivotIndex)
{
	switch ( obj.id ) {
		case "grdStayEndMana":

			if(nCell ==8){

				if(Length(dsStayEndMana.GetColumn(nRow, "FILE_NM")) >0){
					var sLocalFileName  = fcd_GetSelectFile("Save", dsStayEndMana.GetColumn(nRow, "FILE_NM"));
					var sServerFileName = dsStayEndMana.GetColumn(nRow, "FILE_PATH");
					var sFileSize       = dsStayEndMana.GetColumn(nRow, "FILE_SIZE");
					if ( sLocalFileName == null ) return;

					fnc_FileRead(divBtnLst.HttpFile, sLocalFileName[0] + "\\" + sLocalFileName[1], sServerFileName, sFileSize, nRow, "PROG_BAR", dsStayEndMana);

				}
			}else{

				var sUrl 	= "";
				var sArgs 		= "";
				var arrReturn   = array(2);



				sUrl = "frm_rem::REM3011.xml";
				sArgs = "sRaceDt="+quote(dsStayEndMana.GetColumn(nRow,"RACE_DT"))
						+ " sBrncCd="+quote(dsStayEndMana.GetColumn(nRow,"BRNC_CD"))
						+ " sBrncNm="+quote(grdStayEndMana.GetCellText("BODY",nRow,1))
						+ " sMeetCd="+quote(dsStayEndMana.GetColumn(nRow,"MEET_CD"))
						+ " sSeq="+quote(dsStayEndMana.GetColumn(nRow,"SEQ"));	

				arrReturn = Dialog(sUrl, sArgs, 450, 235, "");
			}


			break;

	}


}


