function fcSendMngMsg(svcId){

	  var sSendUser = gdsUser.GetColumn(0,"USER_ID");
	  var sRecvUser = "";
	  var sTitle	= "마감취소";
	  var sDesc		= " ";

	  for(var i = 0; i <dsStayEndMana.GetRowCount();i++){
		if(dsStayEndMana.GetColumn(i,"CHK") == "1"){
			  sRecvUser  =  dsStayEndMana.GetColumn(i,"INST_ID");

		      if(svcId == "saveStayEndCancel"){
				 sDesc      =  fnc_DateFormat(dsStayEndMana.GetColumn(i,"REQ_DT"),"-") + " 일자의 체류인원 등록이 가능합니다. ";
		      }else if(svcId == "saveStayEndReturn"){
				 sDesc      =  fnc_DateFormat(dsStayEndMana.GetColumn(i,"REQ_DT"),"-") + " 일자의 체류인원 마감취소 요청 건이 보류 되었습니다. ";
		      }


			  fcd_SendMessage(sSendUser		// 송신자 사번 
					, sRecvUser					// 수신자 사번 
					, sTitle	    			// 제목
					, "REM3010" 	// 수신자 번호
					,  sDesc					// 수신자명
					, "comSendMsg"
					);							// 내용


		}
	  }
}
