/*-------------------------------------------------------------------------------------------------+
>>  최초 화면 Load時 처리 할 사항
+-------------------------------------------------------------------------------------------------*/
function fcFormLoadSetting(obj) {  

    sBUTTONLIST   = 'TT'+sInpt+'FTFT';

 	//화면환경 설정
	sFORMCAPTION  = obj.Title;
	sFORMLOCATION = "";

	//화면 정보 Display 및 Button 권한 설정
	fnc_FormLoadSetting(obj, sFORMCAPTION, sFORMLOCATION, sBUTTONLIST, sUSERAUTH);

	//if (sApro=="T") cboSearchBrncCd.Enable = true;

    //공통코드 조회
	fcd_GetComboDs(dsInComCd);	
}

/*-------------------------------------------------------------------------------------------------+
>>  최초 화면 UNLoad時 처리 할 사항
+-------------------------------------------------------------------------------------------------*/
function fcFormUnloadProcess(obj) {
	// 자료 변경 여부를 체크하고자 하는 DataSet Object를 넘긴다.
	var sDataset = "dsGoodType";
	if (   !fnc_FormUnloadCheck(sDataset  , sFormId) ) return false;
}

/*-------------------------------------------------------------------------------------------------+
>>  단축키 처리(Form KeyDown)
+-------------------------------------------------------------------------------------------------*/
function fcHotKey(obj,objSenderObj,nChar,bShift,bControl,bAlt,nLLParam,nHLParam) {

	// Return Key Press時 Next Component focus
	if ( nChar == 13 ) {
		var oFocusComponent = GetNextComponent(true);
		oFocusComponent.setfocus();
		return;
	}


}

/***************************************************************************************************
*                                      공통 버튼 Event 처리 부분                                   *
***************************************************************************************************/
/*-------------------------------------------------------------------------------------------------+
>>  취소 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcInit(obj) {

	dsGoodType.ClearData();

}

/*-------------------------------------------------------------------------------------------------+
>>  조회 버튼 클릭 時 필수조건 체크 및 그리드 초기화
+-------------------------------------------------------------------------------------------------*/
function fcSearchCheck(obj) {

	fcInit(obj);

	return true;
}



/*-------------------------------------------------------------------------------------------------+
>>  조회 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcSearch(obj) {

	var sServiceName = "";
	var sInDataSet   = "";
	var sOutDataSet  = "";
	if ( !fcSearchCheck(obj) ) return;


	 sServiceName = "rem5020-service:searchGoodType";
	 sInDataSet   = "";
	 sOutDataSet  = "dsGoodType=dsGoodType ";

	 //조회조건 : 
	 fcd_AddParam("BRNC_CD", cboSearchBrncCd.Value);

	 fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", true);
}


/*-------------------------------------------------------------------------------------------------+
>>  저장 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/

// 저장 전에 체크
function fcSaveCheck(obj) {  
	//변경된 자료가 있는지 체크
	if ( !fnc_DatasetChangeCheck(dsGoodType)) {
		//저장 할 자료가 없음
		fnc_Message(SNIS_COM_1006, "N");	
		return false;
	}

	// 필수항목체크
	oDataSet          = dsGoodType;
	sMandatoryColID   = "GOOD_TYPE_CD";
	sMandatoryColName = "상품분류코드";
	oGrid             = grdGoodType;

	if ( fnc_GetMandatoryViolateRow(oDataSet, sMandatoryColID, sMandatoryColName, oGrid) > -1 ) {
	    return false;
	}

	return true;
}

function fcSave(obj) {

    if ( !fcSaveCheck() ) return;   

    if(fnc_Message(SNIS_COM_1012, "Y")) {           
		var sServiceName = "rem5020-service:saveGoodType";
		var sInDataSet   = "dsGoodType=dsGoodType:u ";
		var sOutDataSet  = "dsGoodType=dsGoodType ";

		fcd_AddParam("BRNC_CD", cboSearchBrncCd.Value);

		fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", false);
    }
}


/*-------------------------------------------------------------------------------------------------+
>>  엑셀 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcToExcel(obj) {
		var sExcelList = "grdGoodType:지점별 상품분류";

		fcd_OpenExcelListExport(sExcelList);
}


/*-------------------------------------------------------------------------------------------------+
>>  화면 종료(닫기) 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcEnd(obj) {
	// 화면 종료
	Close();
}



