/***************************************************************************************************
*                                      사용자 정의  처리 부분                                      *
***************************************************************************************************/
/*-------------------------------------------------------------------------------------------------+
>>  Transaction 후 처리 해야 할 내용!
+-------------------------------------------------------------------------------------------------*/
function fcCallBack(sServiceName, ErrorCode, ErrorMSG) {
	if ( !fnc_result(ErrorCode, ErrorMSG) ) return;

	// 입장인원통계 - 경주별 조회 완료 후
	if ( sServiceName == "rem5020-service:searchGoodType" ) {

	}
	if ( sServiceName = "common-service:searchCode" ) {		
		fcSearch();
	}
}

function fcCboOnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{	
	switch ( obj.id ) {
		case "cboSearchBrncCd":		
			if (cboSearchBrncCd.Value == gdsUser.GetColumn(0, "BRNC_CD") && sApro != "T") {
				grdGoodType.Editable = false;
			}
			setReg("BRNC_CD", cboSearchBrncCd.Value);

			fcSearch();			 
			break;
	}
}

function fcDsOnLoadCompleted(obj,nErrorCode,strErrorMsg,nReason)
{
	switch ( obj.id ) {		
		case "dsBrncCd":		
			dsCommNo.copy(dsBrncCd);
			if (sApro == "T") {
				dsCommNo.InsertRow(0);
				dsCommNo.SetColumn(0, "CD", "%");
				dsCommNo.SetColumn(0, "CD_NM", "전체");
			} else {
				var filterStr = "CD_TERM2='"+GdsUser.GetColumn(0, "TEAM_CD")+"'";
Trace(filterStr);				
				dsCommNo.Filter(filterStr);				
				//cboSearchBrncCd.Value = GdsUser.GetColumn(0, "BRNC_CD");
			}
			//cboSearchBrncCd.index = 0;
			cboSearchBrncCd.Value = getReg("BRNC_CD");
		break;		
	}
}




/*-------------------------------------------------------------------------------------------------+
>>  행추가 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcAddRow(obj) {

	switch( obj.GetForm().id ) {
		case "divBtnRow" :

			nRow = dsGoodType.AddRow();

			dsGoodType.SetColumn(nRow, "COMM_NO", cboSearchBrncCd.Value);	
			dsGoodType.SetColumn(nRow, "USE_YN", "Y");

			break;		
	}
}

/*-------------------------------------------------------------------------------------------------+
>>  행삭제 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcDelRow(obj) {
	//직접 버튼을 클릭한 경우
	switch( obj.GetForm().id ) {
		case "divBtnRow" :
			if ( !fnc_DeleteCheck(dsGoodType, "CHK", "1") ) return;
			break;
	}	
}
