function fcInit(obj) {

	dsGoodStor.ClearData();

}

/*-------------------------------------------------------------------------------------------------+
>>  조회 버튼 클릭 時 필수조건 체크 및 그리드 초기화
+-------------------------------------------------------------------------------------------------*/
function fcSearchCheck(obj) {

	fcInit(obj);

	return true;
}



/*-------------------------------------------------------------------------------------------------+
>>  조회 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcSearch(obj) {

	var sServiceName = "";
	var sInDataSet   = "";
	var sOutDataSet  = "";
	if ( !fcSearchCheck(obj) ) return;


	 sServiceName = "rem5030-service:searchGoodStor";
	 sInDataSet   = "";
	 sOutDataSet  = "dsGoodStor=dsGoodStor ";

	 //조회조건 : 
	 fcd_AddParam("BRNC_CD", cboSearchBrncCd.Value);
 	 fcd_AddParam("STR_DT", calStrDt.Value);
	 fcd_AddParam("END_DT", calEndDt.Value);

	 fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", true);
}


/*-------------------------------------------------------------------------------------------------+
>>  조회 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcSearchGoodType(obj) {

	var sServiceName = "";
	var sInDataSet   = "";
	var sOutDataSet  = "";

	sServiceName = "rem5020-service:searchGoodType";
	sInDataSet   = "";
	sOutDataSet  = "dsGoodType=dsGoodType ";

	//조회조건 : 
	fcd_AddParam("BRNC_CD", cboSearchBrncCd.Value);
	fcd_AddParam("USE_YN",  "Y");

	fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", true);
}


/*-------------------------------------------------------------------------------------------------+
>>  저장 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/

// 저장 전에 체크
function fcSaveCheck(obj) {  

	if ( !fnc_DatasetChangeCheck(dsGoodStor)) {	//변경된 자료가 있는지 체크

		fnc_Message(SNIS_COM_1006, "N");	//저장 할 자료가 없음
		return false;
	}

	// 필수항목체크
	oDataSet          = dsGoodStor;
	sMandatoryColID   = "STOR_DT";
	sMandatoryColName = "입고일자";
	oGrid             = grdGoodStor;

	if ( fnc_GetMandatoryViolateRow(oDataSet, sMandatoryColID, sMandatoryColName, oGrid) > -1 ) {
	    return false;
	}

	return true;
}

function fcSave(obj) {

    if ( !fcSaveCheck() ) return;   

    if(fnc_Message(SNIS_COM_1012, "Y")) {           
		var sServiceName = "rem5030-service:saveGoodStor";
		var sInDataSet   = "dsGoodStor=dsGoodStor:u ";
		var sOutDataSet  = "dsGoodStor=dsGoodStor ";

		fcd_AddParam("BRNC_CD", cboSearchBrncCd.Value);
		fcd_AddParam("STR_DT", calStrDt.Value);
		fcd_AddParam("END_DT", calEndDt.Value);

		fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", false);
    }
}


