/*-------------------------------------------------------------------------------------------------+
>>  Transaction 후 처리 해야 할 내용!
+-------------------------------------------------------------------------------------------------*/
function fcCallBack(sServiceName, ErrorCode, ErrorMSG) {
	if ( !fnc_result(ErrorCode, ErrorMSG) ) return;
	if ( sServiceName = "common-service:searchCode" ) {			// 공통코드조회후 해당 지점의 상품분류 조회
		fcSearchGoodType(this);
	} else if ( sServiceName = "rem5020-service:searchGoodType" ) {	// 상품입고내역 조회후 상품 내역 조회
		fcSearch(this);
	} else if ( sServiceName = "rem5030-service:searchGoodStor" ) {	// 상품입고내역 조회후 상품 내역 조회

	}	
}

function fcCboOnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{	
	switch ( obj.id ) {
		case "cboSearchBrncCd":		
  //Trace("sMnAuthRmk="+sMnAuthRmk);		
  //Trace("cboSearchBrncCd.Value="+cboSearchBrncCd.Value);		
  //Trace("gdsUser.GetColumn(0, BRNC_CD)="+gdsUser.GetColumn(0, "BRNC_CD"));		
			grdGoodStor.Editable = true;
			if (gdsUser.GetColumn(0, "BRNC_CD") == "00" && sApro != "T") {	//본장인 경우
				if (!fnc_isNull(sMnAuthRmk) && sMnAuthRmk != cboSearchBrncCd.Value) {
					grdGoodStor.Editable = false;
				}
			} else if (cboSearchBrncCd.Value != gdsUser.GetColumn(0, "BRNC_CD") && sApro != "T") {
				grdGoodStor.Editable = false;
			}
			// 연간단가계약 품목 선택 버튼은 광명 본장만 가능하게 처리
			var YrItemYn = dsCommNo.GetColumn(cboSearchBrncCd.Index, "CD_TERM3");
			if (Mid(YrItemYn,0,1) == "1") {
				btnYearItemSearch.Visible = true;
				divBtnRow.btnAddRow.Visible = false;
			} else {
				btnYearItemSearch.Visible = false;
				divBtnRow.btnAddRow.Visible = true;
			}
			setReg("BRNC_CD", cboSearchBrncCd.Value);
			fcSearchGoodType();			 
			break;
	}
}

function fcDsOnLoadCompleted(obj,nErrorCode,strErrorMsg,nReason)
{
	switch ( obj.id ) {		
		case "dsBrncCd":
			var i = dsCommNo.copy(dsBrncCd);
			if (sApro == "T") {			
				var nRow = dsCommNo.InsertRow(0);
				dsCommNo.SetColumn(nRow, "CD", "%");
				dsCommNo.SetColumn(nRow, "CD_NM", "전체");

			} else {
				//cboSearchBrncCd.Value = GdsUser.GetColumn(0, "BRNC_CD");
				dsCommNo.Filter("CD_TERM2='"+GdsUser.GetColumn(0, "TEAM_CD")+"'");	
			}
			//cboSearchBrncCd.index = 0;
			cboSearchBrncCd.Value = getReg("BRNC_CD");
			var YrItemYn = dsCommNo.GetColumn(cboSearchBrncCd.Index, "CD_TERM3");
			if (Mid(YrItemYn,0,1) == "1") {
				btnYearItemSearch.Visible = true;
				divBtnRow.btnAddRow.Visible = false;
			} else {
				btnYearItemSearch.Visible = false;
				divBtnRow.btnAddRow.Visible = true;
			}

  //Trace("1.sMnAuthRmk="+sMnAuthRmk);		
  //Trace("1.cboSearchBrncCd.Value="+cboSearchBrncCd.Value);		
  //Trace("1.gdsUser.GetColumn(0, BRNC_CD)="+gdsUser.GetColumn(0, "BRNC_CD"));		
			grdGoodStor.Editable = true;
			if (sApro != "T") {
				if (gdsUser.GetColumn(0, "BRNC_CD") == "00") {	//본장인 경우
					if (!fnc_isNull(sMnAuthRmk) && sMnAuthRmk != cboSearchBrncCd.Value) {
						grdGoodStor.Editable = false;
					}
				} else if (cboSearchBrncCd.Value != gdsUser.GetColumn(0, "BRNC_CD") && sApro != "T") {
					grdGoodStor.Editable = false;
				}
			}
		break;		
	}
}




/*-------------------------------------------------------------------------------------------------+
>>  행추가 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcAddRow(obj) {
	switch( obj.GetForm().id ) {
		case "divBtnRow" :
			//if ( fnc_DatasetChangeCheck(dsGoodStor) ) {
			//	fnc_Message(SNIS_COM_1051, "N");
			//	return;
			//}
			nRow = dsGoodStor.AddRow();			
			dsGoodStor.SetColumn(nRow, "COMM_NO", cboSearchBrncCd.Value);	
			dsGoodStor.SetColumn(nRow, "STOR_DT", today());	
			dsGoodStor.SetColumn(nRow, "USE_YN", "Y");
			dsGoodStor.SetColumn(nRow, "STOR_USER_ID", gdsUser.GetColumn(0, "USER_ID"));	

			break;		
	}
}

/*-------------------------------------------------------------------------------------------------+
>>  행삭제 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcDelRow(obj) {
	//직접 버튼을 클릭한 경우
	switch( obj.GetForm().id ) {
		case "divBtnRow" :
			if ( !fnc_DeleteCheck(dsGoodStor, "CHK", "1") ) return;
			break;
	}	
}

