// 연간 단가계약 품목 조회버튼 클릭시
function fcbtnOnClick(obj)
{
	if (obj.id == "btnYearItemSearch") {

		var args  = "P_COMM_NO=" + quote(cboSearchBrncCd.Value);	
		var bRtn = false;
		if (cboSearchBrncCd.Value == "04" || cboSearchBrncCd.Value == "05") {
			args  = "P_COMM_NO=" + quote("00");	
			bRtn = Dialog("frm_rem::REM5071P.xml", args, 334, 443, "");				// 광명본장의 재고가 있는 수량	
		} else {
			bRtn = Dialog("frm_rem::REM5070P.xml", args, 334, 443, "");			
		}
		var sYearitemSeqNo = "";
		var sStorDt = "";


		if(bRtn) {
			var nRow = 0;
			for (iCnt=0;iCnt<G_ds_return.rowcount();iCnt++) {
				// 기존에 입력된 단가품목이 있는지 확인, 있으면 입력하지 않고 메세지 처리
				sYearitemSeqNo = G_ds_return.GetColumn(iCnt, "YEAR_ITEM_SEQ_NO");
				sStorDt = G_ds_return.GetColumn(iCnt, "STOR_DT");

				var fRow = dsGoodStor.SearchRow("YEAR_ITEM_SEQ_NO='" + sYearItemSeqNo + "' && STOR_DT='" + sStorDt + "'");
				if (fRow != -1) {
					fnc_message("이미 입력된 연간단가품목이 존재합니다.\n\n입고일 : " + sStorDt + "\n품  목 : " + G_ds_return.GetColumn(iCnt, "GOOD_NM"), "N");
					continue;
				}
				nRow = dsGoodStor.AddRow();
				dsGoodStor.SetColumn(nRow, "STOR_DT",           G_ds_return.GetColumn(iCnt, "STOR_DT"));		
				dsGoodStor.SetColumn(nRow, "COMM_NO", 			cboSearchBrncCd.Value);		
				dsGoodStor.SetColumn(nRow, "YEAR_ITEM_SEQ_NO", 	G_ds_return.GetColumn(iCnt, "YEAR_ITEM_SEQ_NO"));		
				dsGoodStor.SetColumn(nRow, "GOOD_TYPE_CD", 		G_ds_return.GetColumn(iCnt, "GOOD_TYPE_CD"));		
				dsGoodStor.SetColumn(nRow, "GOOD_NM", 	     	G_ds_return.GetColumn(iCnt, "GOOD_NM"));		
				dsGoodStor.SetColumn(nRow, "PRICE", 			G_ds_return.GetColumn(iCnt, "PRICE"));		
			}
		}
	}
}


