Window.Script:
/************************
*  공통 Script Include  *
************************/
#include "lib::rbm_common_script.js";
#include "lib::rbm_app_script.js";


/*-----------------------------------+
|  최초 화면 Load時 처리 할 사항     |
+------------------------------------*/
function fcFormLoadSetting(obj){
	calStndDt.Value = today();
	fcSearchGoodType();
}

/*-----------------------+
|  조회 실행 !  |
+------------------------*/
function fcSearch(obj)
{

	var sServiceName = "";
	var sInDataSet   = "";
	var sOutDataSet  = "";


	 sServiceName = "rem5070-service:searchCurYearItemPop";
	 sInDataSet   = "";
	 sOutDataSet  = "dsYearItemPop=dsYearItemPop ";

	 //조회조건 : 
	 fcd_AddParam("BRNC_CD"     , P_COMM_NO);
	 fcd_AddParam("CUR_DT"      , calStndDt.Value);
	 fcd_AddParam("GOOD_TYPE_CD", cboGoodType.Value);
	 fcd_AddParam("ALL_YN"      , chkAll.Value);


	 fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", true);
}
function fcSearchGoodType(obj) {

	var sServiceName = "";
	var sInDataSet   = "";
	var sOutDataSet  = "";

	sServiceName = "rem5020-service:searchGoodType";
	sInDataSet   = "";
	sOutDataSet  = "dsGoodType=dsGoodType ";

	//조회조건 : 
	fcd_AddParam("BRNC_CD", P_COMM_NO);
	fcd_AddParam("USE_YN",  "Y");

	fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", false);
}

/*------------------------------------+
|  Transaction 후 처리 해야 할 내용!  |
+-------------------------------------*/
function fcCallBack(sServiceName, ErrorCode, ErrorMSG) {
	if ( !fnc_result(ErrorCode, ErrorMSG) ) return;

	if ( sServiceName = "rem5020-service:searchGoodType" ) {	// 상품분류 조회후 상품 내역 조회

		fcSearch();
	}
}

/*--------------+
|  확인 처리 !  |
+---------------*/
function fcChoice(obj)
{
	// 신규방식 : G_ds_return으로 return
	var nChkedIdx = dsYearItemPop.FindRow("CHK", 1);
	if(nChkedIdx==-1) {
		fcd_SetReturnDs(dsYearItemPop, dsYearItemPop.GetCurrow());
	} else {
		var sRowIdx;
		for(var i=0; i<dsYearItemPop.GetRowCount(); i++) {
			if(dsYearItemPop.GetColumn(i, "CHK")==1) {
				sRowIdx += i + "^";
			}
		}
		sRowIdx = mid(sRowIdx, 0, length(sRowIdx)-1);
		fcd_SetReturnDs(dsYearItemPop, sRowIdx);
	}
	close(true);
}

/*--------------+
|  취소 처리 !  |
+---------------*/
function fcClose(obj) {
	Close(false);     //Null 반환 후 종료
}

/*---------------------------------+
|  Enter Key Press 시 조회 실행 !  |
+----------------------------------*/
function fcEdsearch_OnKeyDown(obj,nChar,bShift,bCtrl, bAlt,LLParam,HLParam){

	if (nChar==ENTER_KEY) fcSearch();

}

/*-------------------------------------------------------------------------------------------------+
>>  그리드 정렬
+-------------------------------------------------------------------------------------------------*/
function fcGridSort(obj,nCell,nX,nY) {
	//특정 Head 클릭 시 해당 항목으로 Sort(만약 특정 컬럼을 누를때만 소트하고자 한다면 nCell로 조건)
	fnc_GridSort(obj, object(obj.BindDataset), nCell);
}

function fcDsOnColumnChanged(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex)
{
	switch (obj.id )
	{
		case "dsYearItemPop" :
			if( MULTY = "N" ) {
				if(dsYearItemPop.GetColumn(nRow,"CHK") == 1)     //체크박스 선택
				{  

				   for(var i = 0; i<nRow; i++)             //내가 선택한 체크박스 로우에  윗 로우부분과 아래 로우부분은 무조건 0으로 세팅
				   {                                       //예를 들어 내가선택한 로우가  10줄 중에 3번째 줄이면 첫번째줄부터 2번째 줄까지, 4번째 줄에서 10번째 줄까지 0으로 셋팅해줌 
					   dsYearItemPop.SetColumn(i,"CHK",0); 
				   }

				   for(var i = nRow+1; i<dsYearItemPop.GetRowCount(); i++)
				   {
					  dsYearItemPop.SetColumn(i,"CHK",0);
				   }
				}
			}
		break;		
	}
}

function fcGrdOnCellDblClick(obj,nRow,nCell,nX,nY,nPivotIndex)
{
	switch (obj.id ) 
	{
		case "grdYearItemPop" :
			dsYearItemPop.Setcolumn(nRow, "CHK", 1);
			fcChoice(this);
		break;
	}
}
function fcCalOnChanged(obj,strOldText,strNewText)
{
	if (dsGoodType.rowcount() > 0) fcSearch();
}

function fccboOnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
	fcSearch();
}

function dsGoodType_OnLoadCompleted(obj,nErrorCode,strErrorMsg,nReason)
{
	if (dsGoodType.rowcount() > 0) {
		var nRow = dsGoodType.InsertRow(0);
		dsGoodType.SetColumn(nRow, "GOOD_TYPE_CD", "%");
		dsGoodType.SetColumn(nRow, "GOOD_TYPE_NM", "전체");
		cboGoodType.Value = "%";
	}
}

function fcOnClick(obj,strValue)
{
	if (obj.id == "chkAll") {
		if (chkAll.Value == "1") {
			//chkAll.Value = "0";
			chkAll.text = "전체";
		} else {
			//chkAll.Value = "1";
			chkAll.text = "보유";
		}
	}
}
