/*-------------------------------------------------------------------------------------------------+
>>  최초 화면 Load時 처리 할 사항
+-------------------------------------------------------------------------------------------------*/
function fcFormLoadSetting(obj) {  

    sBUTTONLIST   = 'TTFFTFT';

 	//화면환경 설정
	sFORMCAPTION  = obj.Title;
	sFORMLOCATION = "";

	//화면 정보 Display 및 Button 권한 설정
	fnc_FormLoadSetting(obj, sFORMCAPTION, sFORMLOCATION, sBUTTONLIST, sUSERAUTH);

    //공통코드 조회
	fcd_GetComboDs(dsInComCd);		

	//var sCurYear = fcd_GetSysdate("YYYY");	
	//calRaceStrDt.Value = AddDate(today(), -1); 
	calRaceStrDt.Value = substr(fcd_GetSysdate("YYYYMMDD"),0,4)+"0101"; // 현재년도의 1월 1일
	calRaceEndDt.Value = AddDate(today(), -1);

	cboMeetCd.Index = 0;

    fcSearch();
}

/*-------------------------------------------------------------------------------------------------+
>>  최초 화면 UNLoad時 처리 할 사항
+-------------------------------------------------------------------------------------------------*/
function fcFormUnloadProcess(obj) {
	// 자료 변경 여부를 체크하고자 하는 DataSet Object를 넘긴다.
	var sDataset = "dsDivDate";
	if (   !fnc_FormUnloadCheck(sDataset  , sFormId) ) return false;
}

/*-------------------------------------------------------------------------------------------------+
>>  단축키 처리(Form KeyDown)
+-------------------------------------------------------------------------------------------------*/
function fcHotKey(obj,objSenderObj,nChar,bShift,bControl,bAlt,nLLParam,nHLParam) {

	// Return Key Press時 Next Component focus
	if ( nChar == 13 ) {
		var oFocusComponent = GetNextComponent(true);
		oFocusComponent.setfocus();
		return;
	}

}

/***************************************************************************************************
*                                      공통 버튼 Event 처리 부분                                   *
***************************************************************************************************/
/*-------------------------------------------------------------------------------------------------+
>>  취소 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcInit(obj) {

	dsDivDate.ClearData();
	dsDivTms.ClearData();
	dsDivMonth.ClearData();
}

/*-------------------------------------------------------------------------------------------------+
>>  조회 버튼 클릭 時 필수조건 체크 및 그리드 초기화
+-------------------------------------------------------------------------------------------------*/
function fcSearchCheck(obj) {

	fcInit(obj);

	return true;
}



/*-------------------------------------------------------------------------------------------------+
>>  조회 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcSearch(obj) {
    if ( !fcSearchCheck(obj) ) return;

    for (var iCnt=0;iCnt<3;iCnt++) {
		fcSearchAll(iCnt);
    }
}

function fcSearchAll(num) {
	var sServiceName = "";
    var sInDataSet   = "";
    var sOutDataSet  = "";	

	var sMeetCd = "";
	if ((cboMeetCd.Value) == '%'){
        sMeetCd = "";
    }else {
		sMeetCd = cboMeetCd.Value;
    }

	switch (num) {
		case 0 :	//일자별 매출액
			sServiceName = "rem6030-service:searchDivDate";
			sOutDataSet  = "dsDivDate=dsDivDate ";	
			break;
		case 1 : 	//회차별 매출액
			sServiceName = "rem6030-service:searchDivTms";
			sOutDataSet  = "dsDivTms=dsDivTms ";	
			break;
		case 2 :	//월별 매출액
			sServiceName = "rem6030-service:searchDivMonth";
			sOutDataSet  = "dsDivMonth=dsDivMonth ";	
			break;
	} 

	//조회조건 : 
	fcd_AddParam("MEET_CD",			cboMeetCd.Value);
	fcd_AddParam("STR_DT",			calRaceStrDt.Value);
	fcd_AddParam("END_DT",			calRaceEndDt.Value);
	fcd_AddParam("EXCEPT_KM",		chkExceptKM.Value);
	fcd_AddParam("T_ONLY",			chkTOnly.Value);
	fcd_AddParam("MSRCASH",			chkMsrCash.Value);
	fcd_AddParam("FIXED_SEAT",		chkFixedSeat.Value);	 
	fcd_AddParam("BOAT_COUNTRY",	chkBoatCountry.Value);

	fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", true);	
}

/*-------------------------------------------------------------------------------------------------+
>>  엑셀 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcToExcel(obj) {
	var sExcelList = "";
    switch (tab0.TabIndex) {
		case 0:
			sExcelList = "tab0.tab1.grdDivDate:기간별매출액(일자별)"; //데이터셋 정의
			break;
		case 1:		
			sExcelList = "tab0.tab2.grdDivTms:기간별매출액(회차별)"; //데이터셋 정의
			break;
		case 2:
			sExcelList = "tab0.tab4.grdDivMonth:기간별매출액(월별)"; //데이터셋 정의
			break;
    }
    fcd_OpenExcelListExport(sExcelList);
}


/*-------------------------------------------------------------------------------------------------+
>>  화면 종료(닫기) 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcEnd(obj) {
	// 화면 종료
	Close();
}



