/***************************************************************************************************
*                                      사용자 정의  처리 부분                                      *
***************************************************************************************************/
/*-------------------------------------------------------------------------------------------------+
>>  Transaction 후 처리 해야 할 내용!
+-------------------------------------------------------------------------------------------------*/
function fcCallBack(sServiceName, ErrorCode, ErrorMSG) {
	if(ErrorCode <0) {      //oracle 17410 error 발생으로 인한 임시조치
		//ErrorMSG = "처리시간이 지연되고 있습니다. 다시 조회하세요";
		alert(ErrorMSG);
		return;
	}
	if ( !fnc_result(ErrorCode, ErrorMSG) ) return;

	// 입장인원통계 - 경주별 조회 완료 후
	if ( sServiceName == "rem6030-service:searchDivDate" ) {

	}
	if ( sServiceName == "rem6030-service:searchDivTms" ) {

	}
	if ( sServiceName == "rem6030-service:searchDivMonth" ) {

	}

	if ( sServiceName == "common-service:searchCode" ) {
		var nRow = dsMeetCd.InsertRow(0);
		dsMeetCd.SetColumn(nRow, "CD", "%");
		dsMeetCd.SetColumn(nRow, "CD_NM", "전체");
		dsMeetCd.row = nRow;
	}
}




function fcchkOnClick(obj,strValue)
{
	if (obj.id == "chkExceptKM") {
		if (chkExceptKM.Value == "1") {
			chkTOnly.Value = "Y";		
			chkmsrcash.Value = "Y";		
		}
	}
	if (obj.id == "chkTOnly") {
		if (chkTOnly.Value == "N") chkExceptKM.Value = "0";		
	}

	if (obj.id == "chkMsrCash") {
		if (chkMsrCash.Value == "N") chkExceptKM.Value = "0";		
	}

}
