function fcToExcel(obj) {
	var sExcelList = "";
    switch (tab0.TabIndex) {
		case 0:
			sExcelList = "tab0.tab1.grdDivDate:±â°£º°¸ÅÃâ¾×(ÀÏÀÚº°)"; //µ¥ÀÌÅÍ¼Â Á¤ÀÇ
			break;
		case 1:		
			sExcelList = "tab0.tab2.grdDivTms:±â°£º°¸ÅÃâ¾×(È¸Â÷º°)"; //µ¥ÀÌÅÍ¼Â Á¤ÀÇ
			break;
		case 2:
			sExcelList = "tab0.tab4.grdDivMonth:±â°£º°¸ÅÃâ¾×(¿ùº°)"; //µ¥ÀÌÅÍ¼Â Á¤ÀÇ
			break;
    }
    fcd_OpenExcelListExport(sExcelList);
}


