function fcCallBack(sServiceName, ErrorCode, ErrorMSG) {
	if(ErrorCode <0) {      //oracle 17410 error ¹ß»ýÀ¸·Î ÀÎÇÑ ÀÓ½ÃÁ¶Ä¡
		//ErrorMSG = "Ã³¸®½Ã°£ÀÌ Áö¿¬µÇ°í ÀÖ½À´Ï´Ù. ´Ù½Ã Á¶È¸ÇÏ¼¼¿ä";
		alert(ErrorMSG);
		return;
	}
	if ( !fnc_result(ErrorCode, ErrorMSG) ) return;

	// ÀÔÀåÀÎ¿øÅë°è - °æÁÖº° Á¶È¸ ¿Ï·á ÈÄ
	if ( sServiceName == "rem6030-service:searchDivDate" ) {

	}
	if ( sServiceName == "rem6030-service:searchDivTms" ) {

	}
	if ( sServiceName == "rem6030-service:searchDivMonth" ) {

	}

	if ( sServiceName == "common-service:searchCode" ) {
		var nRow = dsMeetCd.InsertRow(0);
		dsMeetCd.SetColumn(nRow, "CD", "%");
		dsMeetCd.SetColumn(nRow, "CD_NM", "ÀüÃ¼");
		dsMeetCd.row = nRow;
	}
}




function fcchkOnClick(obj,strValue)
{
	if (obj.id == "chkExceptKM") {
		if (chkExceptKM.Value == "1") {
			chkTOnly.Value = "Y";		
			chkmsrcash.Value = "Y";		
		}
	}
	if (obj.id == "chkTOnly") {
		if (chkTOnly.Value == "N") chkExceptKM.Value = "0";		
	}

	if (obj.id == "chkMsrCash") {
		if (chkMsrCash.Value == "N") chkExceptKM.Value = "0";		
	}

}
