Window.Script:
#include "lib::rbm_common_script.js";
#include "lib::rbm_app_script.js";

/*********************
*  화면 변수 선언부  *
*********************/
var sEmpNo  = "";
var sEmpNm  = "";
var sDeptCd = "";
/*-----------------------------------+
|  최초 화면 Load時 처리 할 사항     |
+------------------------------------*/
function fcFormLoadSetting(obj){
	dsEmpEnt.ClearData();
    fcSearch();
}

/*-----------------------+
|  조회 실행 !  |
+------------------------*/
function fcSearch(obj)
{	
	var	sServiceName = "rev1010-service:searchEmpEnt";
    var sInDataSet   = "";
    var sOutDataSet  = "dsEmpEnt=dsEmpEnt ";

	fcd_AddParam("EMP_NO", edEmpNo.Text);
	fcd_AddParam("EMP_NM", edEmpNm.Text);

	fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", false);
}

/*------------------------------------+
|  Transaction 후 처리 해야 할 내용!  |
+-------------------------------------*/
function fcCallBack(sServiceName, ErrorCode, ErrorMSG) {
	if ( !fnc_result(ErrorCode, ErrorMSG) ) return;

	// 조회 완료 후
	if ( sServiceName == "rev1010-service:searchEmpEnt" ) {
		edEmpNm.SetFocus();
	}
}

/*--------------+
|  확인 처리 !  |
+---------------*/
function fcChoice(obj)
{
	for( var i=0;i<dsEmpEnt.rowcount();i++ ) {
		if (dsEmpEnt.GetColumn(i,"CHK") == '1') {
			sEmpNo  = dsEmpEnt.GetColumn(i,"EMP_NO");
			sEmpNm  = dsEmpEnt.GetColumn(i,"EMP_NM");
			sDeptCd = dsEmpEnt.GetColumn(i,"DEPT_CD");
		}
	}

	var arrReturn = array(3);               //결과 반환 할 Array	

	arrReturn[0] = sEmpNo;  
	arrReturn[1] = sEmpNm;     
	arrReturn[2] = sDeptCd; 

    Close(arrReturn);                                                 
}

/*--------------+
|  취소 처리 !  |
+---------------*/
function fcClose(obj) {
	Close(false);     //Null 반환 후 종료
}

/*---------------------------------+
|  Enter Key Press 시 조회 실행 !  |
+----------------------------------*/
function fcEdOnKeyDown(obj,nChar,bShift,bCtrl, bAlt,LLParam,HLParam) {
	if ( nChar == ENTER_KEY ) fcSearch();
}

/*-------------------------------------------------------------------------------------------------+
>>  그리드 정렬
+-------------------------------------------------------------------------------------------------*/
function fcGridSort(obj,nCell,nX,nY) {
	//특정 Head 클릭 시 해당 항목으로 Sort(만약 특정 컬럼을 누를때만 소트하고자 한다면 nCell로 조건)
	fnc_GridSort(obj, object(obj.BindDataset), nCell);
}

/*-------------------------------------------------------------------------------------------------+
>>  컬럼 변경 시
+-------------------------------------------------------------------------------------------------*/
function fcDsOnColumnChanged(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex)
{
	switch (obj.id )
	{
		case "dsEmpEnt" :
			if(dsEmpEnt.GetColumn(nRow,"CHK") == 1)     //체크박스 선택
			{  

			   for(var i = 0; i<nRow; i++)             //내가 선택한 체크박스 로우에  윗 로우부분과 아래 로우부분은 무조건 0으로 세팅
			   {                                       //예를 들어 내가선택한 로우가  10줄 중에 3번째 줄이면 첫번째줄부터 2번째 줄까지, 4번째 줄에서 10번째 줄까지 0으로 셋팅해줌 
				   dsEmpEnt.SetColumn(i,"CHK",0); 
			   }
			   for(var i = nRow+1; i<dsEmpEnt.GetRowCount(); i++)
			   {
				  dsEmpEnt.SetColumn(i,"CHK",0);
			   }
			}
		break;		
	}
}

/*-------------------------------------------------------------------------------------------------+
>>  더블클릭
+-------------------------------------------------------------------------------------------------*/
function fcGrdOnCellDblClick(obj,nRow,nCell,nX,nY,nPivotIndex)
{
	switch (obj.id ) 
	{
		case "grdEmpEnt" :
			var arrReturn    = array(2);               //결과 반환 할 Array	

			arrReturn[0] = dsEmpEnt.GetColumn(nRow, "EMP_NO");   
			arrReturn[1] = dsEmpEnt.GetColumn(nRow, "EMP_NM"); 
			arrReturn[2] = dsEmpEnt.GetColumn(nRow, "DEPT_CD");  

			Close(arrReturn);
		break;
	}
}
