//평가대상생성 Check
function fcBtnCheck() {
	var sEvEmp = "";
	var sTitle = tab.tab1.grdEmpEstmExp.GetCellText("Head", 0, 5);

	//작업중인 데이터 존재 시 return
	if ( fnc_DatasetChangeCheck(dsEmpEstm)    ||
		 fnc_DatasetChangeCheck(dsEmpEstm1)   ||
		 fnc_DatasetChangeCheck(dsEmpEstmExp) ||
		 fnc_DatasetChangeCheck(dsEmpEstmMnr) ||
		 fnc_DatasetChangeCheck(dsEmpEstmSvr) ) {
		fnc_Message(SNIS_COM_1052,"N");
		return false;
	}

	//평가자 필수항목 	
	if( dsEmpEstm.RowCount() < 2 ) {
		fnc_Message(SNIS_RBM_F018, "N");
		return false;
	} else {
		for(var i = 0; i < dsEmpEstm.rowcount; i++) {
			if( fnc_IsNull(dsEmpEstm.GetColumn(i, "EMP_NO")) ){
				fnc_Message(SNIS_RBM_F018, "N");
				dsEmpEstmExp.SelectRow(i);
				return false;
			}
		}
	}

	for(var i = 0; i < dsEmpEstmExp.rowcount; i++) {
		sEvEmp = dsEmpEstmExp.GetColumn(i, "ESTM_EMP");
		//피평가자 필수항목
		if( fnc_IsNull( sEvEmp ) &&
			dsEmpEstmExp.GetColumn(i, "ESTM_YN") == "N") {
			fnc_Message(SNIS_RBM_F019, "N", "피평가자", "평가자 성명");
			dsEmpEstmExp.row = i;
			dsEmpEstmExp.SelectRow(i);
			return false;
		}

		//오른쪽 그리드의 평가자가 왼쪽하단 그리드에 없을 때, 평가대상생성 불가능
		if( dsEmpEstmExp.GetColumn(i, "ESTM_WK_JOB") == "1003" ) {
			if( fnc_IsNull(dsEmpEstmExp.GetColumn(i, "RELEA_ESC_YN")) || 
			    dsEmpEstmExp.GetColumn(i, "RELEA_ESC_YN") == "N"    )  {		// 실적평가 제외자가 아닌 경우(2013.11)
				if( dsEmpEstm1.FindRow( "EMP_NO", sEvEmp) == -1 ) {
					dsEmpEstmExp.row = i;
					fnc_Message(SNIS_RBM_F051, "N", i+1, sTitle);
					return false;
				}
			}
		}
	}

	//근무태도평가항목 필수항목
	if( dsAprvMnrSvr.LookUp("APRV_SEQ", "2", "UPDATE_YN") == "Y" ) {
		for(var i = 0; i < dsEmpEstmMnr.rowcount; i++) {
			if( fnc_IsNull( dsEmpEstmMnr.GetColumn(i, "EMP_NO")) ) {
				tab.TabIndex = 1;
				fnc_Message(SNIS_RBM_F019, "N", "근무태도평가항목의 평가자", "성명");
				dsEmpEstmMnr.SelectRow(i);				
				return false;
			}
		}
	}

	//서비스평가항목 필수항목
	if( dsAprvMnrSvr.LookUp("APRV_SEQ", "3", "UPDATE_YN") == "Y" ) {
		for(var i = 0; i < dsEmpEstmSvr.rowcount; i++) {
			if( fnc_IsNull( dsEmpEstmSvr.GetColumn(i, "EMP_NO")) ) {
				tab.TabIndex = 2;
				fnc_Message(SNIS_RBM_F019, "N", "서비스평가항목의 평가자", "성명");
				dsEmpEstmSvr.SelectRow(i);
				return false;
			}
		}
	}
	return true;
}

function fcBtnOnClick(obj) {
	switch(obj.id) {
		case "ApprBtn" : //평가대상생성
			if ( !fcBtnCheck() ) return;

			//부서별평가자 확정이 않된 겯우에는 평가대상생성 불가능
			if( dsCfm.GetColumn(0, "APRV_STAS") != "003" ) {
				fnc_Message(SNIS_RBM_F008, "N");
				return;
			}

			if(fnc_Message(SNIS_RBM_F034, "Y")) {            
				var sServiceName = "rev1070-service:saveAppr";
				var sInDataSet   = "";
				var sOutDataSet  = "";		

				fcEstmYearValue();	

				fcd_AddParam("ESTM_YEAR", sEvalYear);	
				fcd_AddParam("ESTM_NUM" , sEvalNum);		
				fcd_AddParam("DEPT_CD"  , cboDept.Value);
				fcd_AddParam("REGRANT"  , "N");

				fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", true);
			}
			break;
		case "aprvReqBtn" :	//승인요청
			if ( !fcBtnCheck() ) return;

			if(fnc_Message(SNIS_RBM_F032, "Y","승인요청")) {  
				var sServiceName = "rev1050-service:saveAprvMana";
				var sInDataSet   = "";
				var sOutDataSet  = "";    	

				fcEstmYearValue();	

				fcd_AddParam("ESTM_YEAR", sEvalYear);	
				fcd_AddParam("ESTM_NUM" , sEvalNum);
				fcd_AddParam("MENU_ID"  , "REV1070");
				fcd_AddParam("APRVDEPT" , nDeptCd);		//부서
				fcd_AddParam("APRVCD"   , "01");		//승인요청	

				fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", true);
			}
			break;

		case "aprvCallBtn" :	//회수
			if(fnc_Message(SNIS_RBM_F032, "Y","회수")) {  
				var sServiceName = "rev1050-service:saveAprvMana";
				var sInDataSet   = "";
				var sOutDataSet  = "";    	

				fcEstmYearValue();	

				fcd_AddParam("ESTM_YEAR", sEvalYear);	
				fcd_AddParam("ESTM_NUM" , sEvalNum);
				fcd_AddParam("MENU_ID"  , "REV1070");
				fcd_AddParam("APRVDEPT" , nDeptCd);		//부서
				fcd_AddParam("APRVCD"   , "02");		//승인요청	

				fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", true);
			}
			break;
		case "aprvOkBtn" :	//승인
			if(fnc_Message(SNIS_RBM_F032, "Y", "승인")) {  
				var sServiceName = "rev1050-service:saveAprvMana";
				var sInDataSet   = "";
				var sOutDataSet  = "";    	

				fcEstmYearValue();	
				var sCurDate = fcd_GetSysdate("YYYYMMDD");

				fcd_AddParam("ESTM_YEAR", sEvalYear);	
				fcd_AddParam("ESTM_NUM" , sEvalNum);
				fcd_AddParam("MENU_ID"  , "REV1070");
				fcd_AddParam("APRVDEPT" , nDeptCd);		//부서
				fcd_AddParam("APRVCD"   , "03");			//승인요청	
				fcd_AddParam("APRVDATE" , sCurDate);		//승인날짜

				fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", true);
			}
			break;
		case "aprvBackBtn" :	//반려
			if(fnc_Message(SNIS_RBM_F032, "Y", "반려")) {  
				var sServiceName = "rev1050-service:saveAprvMana";
				var sInDataSet   = "";
				var sOutDataSet  = "";    	

				fcEstmYearValue();	

				fcd_AddParam("ESTM_YEAR", sEvalYear);	
				fcd_AddParam("ESTM_NUM" , sEvalNum);
				fcd_AddParam("MENU_ID"  , "REV1070");
				fcd_AddParam("APRVDEPT" , nDeptCd);		//부서
				fcd_AddParam("APRVCD"   , "04");							//승인요청	

				fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", true);
			}
			break;
		case "aprvCanCelBtn" :	//승인취소
			if(fnc_Message(SNIS_RBM_F032, "Y", "승인취소")) {  
				var sServiceName = "rev1050-service:saveAprvMana";
				var sInDataSet   = "";
				var sOutDataSet  = "";    	

				fcEstmYearValue();	

				fcd_AddParam("ESTM_YEAR", sEvalYear);	
				fcd_AddParam("ESTM_NUM" , sEvalNum);
				fcd_AddParam("MENU_ID"  , "REV1070");
				fcd_AddParam("APRVDEPT" , nDeptCd);		//부서
				fcd_AddParam("APRVCD"   , "05");		//승인요청	

				fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", true);
			}
			break;
	}
}

/*-------------------------------------------------------------------------------------------------+
>>  중복여부 체크
+-------------------------------------------------------------------------------------------------*/
function fcDsOnColumnChanged(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex)
{
	switch( obj.id ) {
		case "dsEmpEstm" : //매점 평가자는 업무수행 평가자와 다른 사람이 지정되어야 한다.
			if ( strColumnID == "EMP_NO" ) {
				obj.FireEvent = false;
				if( !fnc_IsNull(varNewValue) ) {
					if ( fnc_GetExistValue(obj, strColumnID, varNewValue) != nRow ) {
						fnc_Message(L_COM_ALT_0016, "N", "성명이");
						obj.SetColumn(nRow, "JOB_TIT_NM" , "");
						obj.SetColumn(nRow, "EMP_NM"	 , "");
						obj.SetColumn(nRow, "DEPT_CD"	 , "");
						obj.SetColumn(nRow, strColumnID  , "");
					}
				}
				obj.FireEvent = true;
			}
			break;
		case "dsEmpEstm1" :
			if ( strColumnID == "EMP_NO" ) {
				obj.FireEvent = false;
				if( !fnc_IsNull(varNewValue) ) {
					if ( fnc_GetExistValue(obj, strColumnID, varNewValue) != nRow ) {
						fnc_Message(L_COM_ALT_0016, "N", "성명이");

						obj.SetColumn(nRow, "TEAM_CD"  , "");
						obj.SetColumn(nRow, "HP_NO"    , "");
						obj.SetColumn(nRow, "HP_NO"    , "");
						obj.SetColumn(nRow, "WK_JOB_NM", "");
						obj.SetColumn(nRow, "ESTM_WK_JOB", "");
						obj.SetColumn(nRow, "EMP_NM"   , "");
						obj.SetColumn(nRow, strColumnID, "");
					}
					if ( fnc_GetExistValue(obj, strColumnID, varNewValue, nRow + 1) > 0 ) {
						fnc_Message(L_COM_ALT_0016, "N", "성명이");
						obj.SetColumn(nRow, "TEAM_CD"  , "");
						obj.SetColumn(nRow, "HP_NO"    , "");
						obj.SetColumn(nRow, "HP_NO"    , "");
						obj.SetColumn(nRow, "WK_JOB_NM", "");
						obj.SetColumn(nRow, "ESTM_WK_JOB", "");
						obj.SetColumn(nRow, "EMP_NM"   , "");
						obj.SetColumn(nRow, strColumnID, "");
					}
				}
				obj.FireEvent = true;
			}
		break;
	}
}

function fcCboOnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
	if (strText != '') fcSearch(obj);
}
