var sFormId       = this.GetForm().id;
var sBUTTONLIST   = 'FFFFTFT';                         //공통기능정의(버튼순으로 T or F)
var sUSERAUTH     = '';                                //사용자별 프로그램 사용 권한
var sFORMCAPTION  = '';                                //Form의 Title 정보
var sFORMLOCATION = '';                                //Form의 Location 정보
var sUSERLAVEL    = '';                                //Form의 사용  Level
var vREVOBJ       = Array(9);                          //결과값 처리를 위한 오브젝트 
var sGRDTIT       = "";                                //그리드 제목
var sCUROBJ       = "";                                //현재 LOST FOCUS 오브젝트

var sMenuGrpNum     = "0";                         	   // 메뉴그룹의 갯수보관
var sMenuListNum    = "0";                             // 메뉴리스트의 갯수보관
var sMenuUpId       = "";
var sMenuUpNm       = "";
var sMsg            = "";								// 삭제 불가능 row 보관

var sEvalYear = "";
var sEvalNum  = "";
/***************************************************************************************************
*                                         Form 처리 부분                                           *
***************************************************************************************************/
/*-------------------------------------------------------------------------------------------------+
>>  최초 화면 Load時 처리 할 사항
+-------------------------------------------------------------------------------------------------*/
function fcFormLoadSetting(obj) {  
    sBUTTONLIST   = 'FTTFTFT';

 	//화면환경 설정
	sFORMCAPTION  = obj.Title;
	sFORMLOCATION = "";

	//화면 정보 Display 및 Button 권한 설정
	fnc_FormLoadSetting(obj, sFORMCAPTION, sFORMLOCATION, sBUTTONLIST, sUSERAUTH);

    //공통코드 조회
    fcd_GetComboDs(dsInComCd);

	dsEstmAltYnSearch.InsertRow(0);
	dsEstmAltYnSearch.SetColumn(0, "CD_NM", "전체");
	cboEstmAltYn.index = 0;

	fcSearchComboDept();
}

/*-------------------------------------------------------------------------------------------------+
>>  최초 화면 UNLoad時 처리 할 사항
+-------------------------------------------------------------------------------------------------*/
function fcFormUnloadProcess(obj) {
	//자료 변경 여부를 체크하고자 하는 DataSet Object를 넘긴다.
	var sDataset = "dsEvEmp";
	if ( !fnc_FormUnloadCheck(sDataset, sFormId) ) return false;
}

/***************************************************************************************************
*                                      업무화면 공통 버튼 Event 처리 부분                          *
***************************************************************************************************/
/*-------------------------------------------------------------------------------------------------+
>>  초기화 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcInit(obj) {
    dsEvEmp.ClearData();  
}

/*-------------------------------------------------------------------------------------------------+
>>  조회 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcSearchCheck(obj) {
	fcInit(obj);
	return true;
}

function fcSearch(obj) {
    if ( !fcSearchCheck(obj) ) return;

	var sServiceName = "rev1100-service:searchEvEmp";
    var sInDataSet   = "";
    var sOutDataSet  = "dsEvEmp=dsEvEmp dsAprvStas=dsAprvStas ";     

    fcEstmYearValue();

    //조회조건
	fcd_AddParam("ESTM_YEAR"   , sEvalYear);
	fcd_AddParam("ESTM_NUM"    , sEvalNum);
	fcd_AddParam("DEPT_CD"     , cboDept.Value);
	fcd_AddParam("EMP_NM"      , edEmpNm.Text);
	fcd_AddParam("ESTM_ALT_YN" , cboEstmAltYn.Value);

	fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", true);
}

// 부서조회
function fcSearchComboDept(obj)
{
	dsDept   .ClearData();
	dsDeptGrd.ClearData();

	var sServiceName = "rev1050-service:searchAllDept";
    var sInDataSet   = "";
    var sOutDataSet  = "dsDept=dsAllDept dsDeptGrd=dsAllDept";     

	fcEstmYearValue();	

	fcd_AddParam("ESTM_YEAR", sEvalYear);	
	fcd_AddParam("ESTM_NUM" , sEvalNum);	

	fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", false);
}

/*-------------------------------------------------------------------------------------------------+
>>  저장 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/

// 저장 전에 체크
function fcSaveCheck(obj) {

	//변경된 자료가 있는지 체크
	if ( !fnc_DatasetChangeCheck(dsEvEmp) ) {
		//저장 할 자료가 없음
		fnc_Message(SNIS_COM_1006, "N");	
		return false;
	}

	for(var i=0; i<dsEvEmp.rowcount;i++) {
		if( dsEvEmp.GetRowType(i) == "update" ) {			
			if( dsEvEmp.GetColumn(i, "ESTM_ALT_YN") == "Y" &&
				fnc_IsNull(dsEvEmp.GetColumn(i, "ESTM_ESC_RSN"))) {
				fnc_Message(SNIS_RBM_F049 , "N", i+1);
				return false;
			}	
		}
	}
	return true;
}

function fcSave(obj) {
    if ( !fcSaveCheck() ) return;  

    var sServiceName = "rev1100-service:saveEvEmp";
    var sInDataSet   = "dsEvEmp=dsEvEmp:u ";
    var sOutDataSet  = "";

    fcEstmYearValue();	

	fcd_AddParam("ESTM_YEAR", sEvalYear);	
	fcd_AddParam("ESTM_NUM" , sEvalNum);

    fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", true);
}

/*-------------------------------------------------------------------------------------------------+
>>  엑셀 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcToExcel(obj) {
    var sExcelList = "grdEvEmp:평가대상조정";
    fcd_OpenExcelListExport(sExcelList);
}

/*-------------------------------------------------------------------------------------------------+
>>  프린트 인쇄 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcPrint(obj) {
}

/*-------------------------------------------------------------------------------------------------+
>>  화면 종료(닫기) 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcEnd(obj) {
	//화면 종료
	Close();	
}



