>>  Help Popup Open
+-------------------------------------------------------------------------------------------------*/
function fcHelpDialoge(obj,nX,nY) {

}


/***************************************************************************************************
*                                      사용자 정의  처리 부분                                      *
***************************************************************************************************/
/*-------------------------------------------------------------------------------------------------+
>>  Transaction 후 처리 해야 할 내용!
+-------------------------------------------------------------------------------------------------*/
function fcCallBack(sServiceName, ErrorCode, ErrorMSG) {
	if ( !fnc_result(ErrorCode, ErrorMSG) ) return;

	// 조회 완료 후
	if ( sServiceName == "rev1100-service:searchEvEmp" ) {
		if( dsAprvStas.GetColumn(0, "APRV_YN") == "Y" ) {
			//변경가능
			divBtnLst.btnSave.Enable = true;
			grdEvEmp.Editable = true;
		} else {
			//변경불가능
			divBtnLst.btnSave.Enable = false;
			grdEvEmp.Editable = false;
		}

	}

	//  부서 콤보 조회 완료 후
	if ( sServiceName == "rev1050-service:searchAllDept" ) {
		dsDept.InsertRow(0);
		dsDept.SetColumn(0, "DEPT_NM", "전체");
		cboDept.index = 0;

		fcSearch();
	}

	// 저장 후
	if ( sServiceName == "rev1100-service:saveEvEmp" ) {
		//fcSearch();
	}
}

function fcEstmYearValue(obj)
{
	/* 콤보박스에서 값 가져오기 */
    var nCbValue =cbEstmYear.Value;
	var nCbRow = dsEstmYear.SearchRow("CD=="+nCbValue);

	sEvalYear = dsEstmYear.GetColumn(nCbRow,"ESTM_YEAR");
	sEvalNum = dsEstmYear.GetColumn(nCbRow,"ESTM_NUM");

}

function fcDsOnColumnChanged(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex)
{
	switch( obj.id ) {
		case "dsEvEmp" :
			if( strColumnID == "ESTM_ALT_YN" ) {
				if( varNewValue == "N" ) {
					obj.SetColumn(nRow, "ESTM_ESC_RSN", "");
				}
			}
		break;
	}
}

function fcCheckCombo(obj,strCode,strText,nOldIndex,nNewIndex)
{
	fcSearchComboDept();
}
