Window.Script:
#include "lib::rbm_common_script.js";
#include "lib::rbm_app_script.js";

/*********************
*  화면 변수 선언부  *
*********************/
var sEstmYear;
var sEstmNum;
var sBrncCd;
var sBrncNm;
var sEmpNO;
var sEmpNm;
var sTellerId;

/*-----------------------------------+
|  최초 화면 Load時 처리 할 사항     |
+------------------------------------*/
function fcFormLoadSetting(obj){
	sEstmYear = ESTM_YEAR;
    sEstmNum = ESTM_NUM;

	//공통코드 조회
	fcd_GetComboDs(dsInComCd);

	dsCommNo.InsertRow(0);
	dsCommNo.SetColumn(0, "CD_NM", "전체");
	if (ESTM_DEPT_CD != "") {
		cboCommNoP.value = ESTM_DEPT_CD;
		cboCommNoP.Enable = false;
	} else {
		cboCommNoP.Index = 0;
	}

	fcSearch();
}

/*-----------------------+
|  조회 실행 !  |
+------------------------*/
function fcSearchCheck(obj) {
    dsTeller.ClearData();
    return true;
}

function fcSearch(obj)
{	
	if ( !fcSearchCheck(obj) ) return;

	var	sServiceName = "rev3200-service:searchTeller";
    var sInDataSet   = "";
    var sOutDataSet  = "dsTeller=dsTeller ";

	fcd_AddParam("ESTM_YEAR", sEstmYear);
	fcd_AddParam("ESTM_NUM" , sEstmNum);
	fcd_AddParam("BRNC_CD"  , cboCommNoP.Value);
	fcd_AddParam("EMP_NM"   , edEmpNm.Text);

	fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", true);
}

/*------------------------------------+
|  Transaction 후 처리 해야 할 내용!  |
+-------------------------------------*/
function fcCallBack(sServiceName, ErrorCode, ErrorMSG) {
	if ( !fnc_result(ErrorCode, ErrorMSG) ) return;

	// 조회 완료 후
	if ( sServiceName == "rev3200-service:searchTeller" ) {
		edEmpNm.SetFocus();
	}
}

/*--------------+
|  확인 처리 !  |
+---------------*/
function fcChoice(obj) {
	nRow = dsTeller.FindRow("CHK", "1");

	sBrncCd     = dsTeller.GetColumn(nRow, "BRNC_CD");
	sBrncNm     = dsTeller.GetColumn(nRow, "BRNC_NM");
	sEmpNO      = dsTeller.GetColumn(nRow, "EMP_NO");
	sEmpNm      = dsTeller.GetColumn(nRow, "EMP_NM");
	sTellerId   = dsTeller.GetColumn(nRow, "TELLER_ID");

	var arrReturn = array(5);  //결과 반환 할 Array	

	arrReturn[0] = sBrncCd;  
	arrReturn[1] = sBrncNm;     
	arrReturn[2] = sEmpNO; 
	arrReturn[3] = sEmpNm; 
	arrReturn[4] = sTellerId; 

    Close(arrReturn);                                                 
}

/*--------------+
|  취소 처리 !  |
+---------------*/
function fcClose(obj) {
	Close(false);     //Null 반환 후 종료
}

/*---------------------------------+
|  Enter Key Press 시 조회 실행 !  |
+----------------------------------*/
function fcEdOnKeyDown(obj,nChar,bShift,bCtrl, bAlt,LLParam,HLParam) {
	if ( nChar == ENTER_KEY ) fcSearch();
}

/*-------------------------------------------------------------------------------------------------+
>>  그리드 정렬
+-------------------------------------------------------------------------------------------------*/
function fcGridSort(obj,nCell,nX,nY) {
	//특정 Head 클릭 시 해당 항목으로 Sort(만약 특정 컬럼을 누를때만 소트하고자 한다면 nCell로 조건)
	fnc_GridSort(obj, object(obj.BindDataset), nCell);
}

/*-------------------------------------------------------------------------------------------------+
>>  컬럼 변경 시
+-------------------------------------------------------------------------------------------------*/
function fcDsOnColumnChanged(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex) {
	if( strColumnID == "CHK" && varNewValue == 1 ) {
		for(var i=0;i<obj.rowcount;i++) {
			if(i != nRow)	obj.SetColumn(i,"CHK",0); 
		}
	}
}

/*-------------------------------------------------------------------------------------------------+
>>  더블클릭
+-------------------------------------------------------------------------------------------------*/
function fcGrdOnCellDblClick(obj,nRow,nCell,nX,nY,nPivotIndex) {
	var arrReturn    = array(4);  

	arrReturn[0] = dsTeller.GetColumn(nRow, "BRNC_CD");
	arrReturn[1] = dsTeller.GetColumn(nRow, "BRNC_NM");
	arrReturn[2] = dsTeller.GetColumn(nRow, "EMP_NO");
	arrReturn[3] = dsTeller.GetColumn(nRow, "EMP_NM");
	arrReturn[4] = dsTeller.GetColumn(nRow, "TELLER_ID");
	Close(arrReturn);
}
