function fcSearchCurrRow(obj) {
    if ( !fcSearchCurrRowCheck(obj) ) return;

	var sServiceName = "e02040010-service:searchArrangeCurrRow";
    var sInDataSet   = "";
    var sOutDataSet  = "dsOutArrangeCurrRow=dsOutArrangeCurrRow ";

	fce_AddParam(Trim("STND_YEAR"), cboSStndYear.Value);
	fce_AddParam(Trim("MBR_CD   "), cboSMbr.Value     );
	fce_AddParam(Trim("TMS      "), cboSRaceTms.Value );
	fce_AddParam(Trim("RACER_NO "), dsOutArrange.GetColumn(dsOutArrange.currow, "RACER_NO"));
	fce_AddParam(Trim("MOT_NO   "), dsOutArrange.GetColumn(dsOutArrange.currow, "MOT_NO"  ));
	fce_AddParam(Trim("BOAT_NO  "), dsOutArrange.GetColumn(dsOutArrange.currow, "BOAT_NO" ));

	fce_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack");
}

/*-------------------------------------------------------------------------------------------------+
>>  ÀúÀå ¹öÆ° Å¬¸¯ ãÁ Ã³¸®
+-------------------------------------------------------------------------------------------------*/
function fcSaveCheck(obj) {

    var nTmsRow        = fnc_GetExistValue(dsOutSRaceTms, "TMS", cboSRaceTms.Value);  // 
    var sState         = dsOutSRaceTms.GetColumn(nTmsRow, "RACE_TMS_STAT_CD");           // ÇØ´ç È¸Â÷ »óÅÂ

    if ( nTmsRow < 0 ) {
		fnc_Message(SNIS_BOA_B005, "N");
		return false;
    }

    if ( sState >= "050" ) {
		// fnc_Message(SNIS_BOA_B009, "N");
		// return false;
    }

    if ( !fnc_IsMandatory(cboSSpec, "Æ¯¼±±×·ì") ) return false;

    return true;
}

function fcSave(obj) {
    if ( !fcSaveCheck(obj) ) return;

	var sServiceName = "e02040010-service:saveArrangeChng";
    var sInDataSet   = "dsOutArrange=dsOutArrange:u ";
    var sOutDataSet  = "dsOutArrange=dsOutArrange ";

	fce_AddParam(Trim("STND_YEAR"), cboSStndYear.Value);
	fce_AddParam(Trim("MBR_CD   "), cboSMbr.Value     );
	fce_AddParam(Trim("TMS      "), cboSRaceTms.Value );
	fce_AddParam(Trim("SPEC_CD  "), cboSSpec.Value    );

	fce_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack");
}

