/*-------------------------------------------------------------------------------------------------+
>>  Transaction ÈÄ Ã³¸® ÇØ¾ß ÇÒ ³»¿ë!
+-------------------------------------------------------------------------------------------------*/
function fcCallBack(sServiceName, ErrorCode, ErrorMSG) {

	if ( !fnc_Result(ErrorCode, ErrorMSG) ) return;

	// °øÅëÄÚµå Á¶È¸½Ã
	if ( sServiceName == "common-service:searchCode" ) {
        cboSMbr.Index = 0;
        cboSMbr.Value = fce_GetUserInfo("MBR_CD");
        cboSSpec.Index = 0;

        fce_GetRaceTms(cboSStndYear.Value, cboSMbr.Value, "", "dsOutSRaceTms");
		return;
	}

	// È¸Â÷ ¸®½ºÆ® Á¶È¸½Ã
	if ( sServiceName == "common_race-service:searchRaceTms" ) {
        cboSRaceTms.Index = 0;
        if ( cboSStndYear.Value == fce_GetCurRaceInfo("STND_YEAR") ) {
            var nIndexRow = fnc_GetExistValue(dsOutSRaceTms, "TMS", fce_GetCurRaceInfo("TMS"));
            if ( nIndexRow > -1 ) cboSRaceTms.Index = nIndexRow;
        }
        cboSRaceTms_OnChanged();
		fce_GetRaceDayOrd(cboSStndYear.Value, cboSMbr.Value, cboSRaceTms.Value, "dsOutSDayOrd");
		return;
	}

	// ÀÏÂ÷ ¸®½ºÆ® Á¶È¸½Ã
	if ( sServiceName == "common_race-service:searchRaceDayOrd" ) {
		return;
	}

	// Á¶È¸ ÈÄ
	if ( sServiceName == "e02040010-service:searchArrange" ) {
		return;
	}

	// ÇöÀçRowÁ¶È¸ ÈÄ
	if ( sServiceName == "e02040010-service:searchArrangeCurrRow" ) {

        var nRow = dsOutArrange.currow;
        var nSourceRow = 0;
        dsOutArrange.SetColumn(nRow, Trim("RACER_NO                       "), dsOutArrangeCurrRow.GetColumn(nSourceRow, Trim("RACER_NO                       ")));
        dsOutArrange.SetColumn(nRow, Trim("RACER_GRD_CD                   "), dsOutArrangeCurrRow.GetColumn(nSourceRow, Trim("RACER_GRD_CD                   ")));
        dsOutArrange.SetColumn(nRow, Trim("NM_KOR                         "), dsOutArrangeCurrRow.GetColumn(nSourceRow, Trim("NM_KOR                         ")));
        dsOutArrange.SetColumn(nRow, Trim("RACER_STAT_CD                  "), dsOutArrangeCurrRow.GetColumn(nSourceRow, Trim("RACER_STAT_CD                  ")));
        dsOutArrange.SetColumn(nRow, Trim("FAVOR_COURSE_CD                "), dsOutArrangeCurrRow.GetColumn(nSourceRow, Trim("FAVOR_COURSE_CD                ")));
        dsOutArrange.SetColumn(nRow, Trim("EMAIL_ADDR                     "), dsOutArrangeCurrRow.GetColumn(nSourceRow, Trim("EMAIL_ADDR                     ")));
        dsOutArrange.SetColumn(nRow, Trim("RACER_PERIO_NO                 "), dsOutArrangeCurrRow.GetColumn(nSourceRow, Trim("RACER_PERIO_NO                 ")));
        dsOutArrange.SetColumn(nRow, Trim("TRRAS_TMS_6_AMU_RANK_SCR       "), dsOutArrangeCurrRow.GetColumn(nSourceRow, Trim("TRRAS_TMS_6_AMU_RANK_SCR       ")));
        dsOutArrange.SetColumn(nRow, Trim("TRRAS_TMS_6_AVG_RANK_SCR       "), dsOutArrangeCurrRow.GetColumn(nSourceRow, Trim("TRRAS_TMS_6_AVG_RANK_SCR       ")));
        dsOutArrange.SetColumn(nRow, Trim("TRRAS_TMS_6_AMU_ACDNT_SCR      "), dsOutArrangeCurrRow.GetColumn(nSourceRow, Trim("TRRAS_TMS_6_AMU_ACDNT_SCR      ")));
        dsOutArrange.SetColumn(nRow, Trim("TRRAS_TMS_6_AVG_ACDNT_SCR      "), dsOutArrangeCurrRow.GetColumn(nSourceRow, Trim("TRRAS_TMS_6_AVG_ACDNT_SCR      ")));
        dsOutArrange.SetColumn(nRow, Trim("TRRAS_TMS_6_AVG_SCR            "), dsOutArrangeCurrRow.GetColumn(nSourceRow, Trim("TRRAS_TMS_6_AVG_SCR            ")));
        dsOutArrange.SetColumn(nRow, Trim("TRRAS_TMS_6_WIN_RATIO          "), dsOutArrangeCurrRow.GetColumn(nSourceRow, Trim("TRRAS_TMS_6_WIN_RATIO          ")));
