/*-------------------------------------------------------------------------------------------------+
>>  È¸Â÷º¯°æ½Ã
+-------------------------------------------------------------------------------------------------*/
function cboSRaceTms_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
    fcInit();

    var nRow = fnc_GetExistValue(dsOutSRaceTms, "TMS", cboSRaceTms.Value);
    var nDayOrd = toInteger(dsOutSRaceTms.GetColumn(nRow, "DAY_ORD"));
	if(nDayOrd == 3)  { //3È¸Â÷ º¸ÀÌµµ·Ï ¼öÁ¤
		grdArrange.autofit = false;
		grdArrange.SetColProp(10, "Width", 25);
	}
	if(nDayOrd < 3)  { //3È¸Â÷ ¾Êº¸ÀÌµµ·Ï ¼öÁ¤
		grdArrange.autofit = false;
		grdArrange.SetColProp(10, "Width", 0);
	} 	
/*
    for ( var i = 0; i < nDayOrd; i++ ) {
        grdArrange.SetColProp(8 + i, "Width", 25);
        grdArrange.SetColProp(34 + (i * 2), "Width", 50);
        grdArrange.SetColProp(35 + (i * 2), "Width", 50);
    }
    for ( var i = nDayOrd; i < 7; i++ ) {
        grdArrange.SetColProp(8 + i, "Width",  0);
        grdArrange.SetColProp(34 + (i * 2), "Width", 0);
        grdArrange.SetColProp(35 + (i * 2), "Width", 0);
    }
*/
	if ( dsOutSRaceTms.GetColumn(nRow, "RACE_TMS_STAT_CD") < "055" ) {
        btnBoatChng.Enable  = true;
        btnMotChng.Enable   = true;
        btnRacerChng.Enable = true;
        divBtnLst.btnSave.Enable = true;
	} else {
        btnBoatChng.Enable  = false;
        btnMotChng.Enable   = false;
        btnRacerChng.Enable = false;
        divBtnLst.btnSave.Enable = false;
	}
}
