function fcInit(obj) {

    dsRaceInfo.ClearData();
    dsVeriFstSDLDTSatlit.ClearData();
    dsVeriFstSDLDTSellst.ClearData();
    dsVeriFstSDLDTTelmp.ClearData();
    dsSATLITSUM.ClearData();
}

/*-------------------------------------------------------------------------------------------------+
>>  조회 버튼 클릭 時 체크 처리
+-------------------------------------------------------------------------------------------------*/
function fcSearchCheck(obj) {
    //조회조건 체크및 trim처리

	fcInit(obj);    //기존 데이터셋 초기화

	return true;
}


/*-------------------------------------------------------------------------------------------------+
>>  년도별 최근 회차
+-------------------------------------------------------------------------------------------------*/
function fcSearchTms(obj) {
    if ( !fcSearchCheck(obj) ) return;

	var sServiceName = "rsm1050-service:searchTms"; // 최근 경주 결과 날짜
    var sInDataSet   = "";
    var sOutDataSet  = "dsTms=dsTms ";

	dsTms.ClearData();
	fcd_AddParam("MEET_CD", rdoMeetCd.Value);		// 개최지
	fcd_AddParam("STND_YEAR", cboStndYear.Value);
	fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack",false);
}

/*-------------------------------------------------------------------------------------------------+
>>  년도별 최근 회차별 최근 일차
+-------------------------------------------------------------------------------------------------*/
function fcSearchDayOrd(obj) {
    if ( !fcSearchCheck(obj) ) return;

	var sServiceName = "rsm1050-service:searchDayOrd"; // 최근 경주 결과 날짜

    var sInDataSet   = "";
    var sOutDataSet  = "dsDayOrd=dsDayOrd ";

	dsDayOrd.ClearData();
	fcd_AddParam("MEET_CD", rdoMeetCd.Value);		// 개최지
	fcd_AddParam("STND_YEAR", cboStndYear.Value);
	fcd_AddParam("TMS", cboTms.Value);
	fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack",false);
}


/*-------------------------------------------------------------------------------------------------+
>>  fcSearch 처리
+-------------------------------------------------------------------------------------------------*/
function fcSearch(obj) {
    if ( !fcSearchCheck(obj) ) return;

	var sServiceName = "rsm1050-service:searchRaceDayInfo";
    var sInDataSet   = "";
    var sOutDataSet  = "dsRaceInfo=dsRaceInfo ";
		sOutDataSet  += "dsVeriFstSDLDTSatlit=dsVeriFstSDLDTSatlit ";
        sOutDataSet += "dsVeriFstSDLDTSellst=dsVeriFstSDLDTSellst ";    
        sOutDataSet += "dsVeriFstSDLDTTelmp=dsVeriFstSDLDTTelmp ";    
		sOutDataSet += "dsSATLITSUM=dsSATLITSUM ";    


	fcd_AddParam("STND_YEAR", cboStndYear.Value);
	fcd_AddParam("MEET_CD", rdoMeetCd.Value);
	fcd_AddParam("TMS", cboTms.Value);
	fcd_AddParam("DAY_ORD", cboDayOrd.Value);
	fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack",true);

}


