/***************************************************************************************************
*                                      사용자 정의  처리 부분                                      *
***************************************************************************************************/
/*-------------------------------------------------------------------------------------------------+
>>  Transaction 후 처리 해야 할 내용!
+-------------------------------------------------------------------------------------------------*/
function fcCallBack(sServiceName, ErrorCode, ErrorMSG) {

    if ( !fnc_result(ErrorCode, ErrorMSG) ) return;

    if ( sServiceName == "rsm1050-service:searchRaceDayInfo" ) { // TMS 값 검색
		dsVeriFstSDLDTSatlit.row = -1;
		dsVeriFstSDLDTSellst.row = -1;
		dsVeriFstSDLDTTelmp.row = -1;
		dsSATLITSUM.row = -1;
		if (dsVeriFstSDLDTSellst.RowCount() == dsVeriFstSDLDTSellst.Sum("TF2")) {
			grdVeriFstSDLDTSellst.SetCellProp("head",3,"bkColor","User Color 20");
		} else {
			grdVeriFstSDLDTSellst.SetCellProp("head",3,"BkColor","#fdbfbf");
		}
		return;
	}
    if ( sServiceName == "rsm1050-service:searchTms" ) { // TMS 값 검색

		if(dsTms.GetRowCount()==0)
		{		
			dsTms.SetColumn(0,"TMS","");
			dsTms.SetColumn(0,"TMS_DESC","없음");
			cboTms.Index=0;
		}
		else
		{
			if(TMS != "")
			{
				cboTms.Value = TMS;
				TMS="";
			}
			else
			{		
				cboTms.Value = dsTms.GetColumn(0,"MAX_TMS");		
			}
		}

		fcSearchDayOrd();
		return;
	}

	if ( sServiceName == "rsm1050-service:searchDayOrd" ) {		

		if(dsDayOrd.GetRowCount()==0)
		{
			dsDayOrd.AddRow();
			dsDayOrd.SetColumn(0,"DAY_ORD","");
			dsDayOrd.SetColumn(0,"DAY_DESC","없음");
			cboDayOrd.Index=0;
		}
		else
		{
			if(DAY_ORD != "")
			{
				cboDayOrd.Value = DAY_ORD;
				DAY_ORD="";
			}
			else
			{
				cboDayOrd.Value = dsDayOrd.GetColumn(0,"MAX_DAY_ORD");
			}
		}

		fcSearch();
		return;
	}

	var sMonth = substr(dsRaceInfo.GetColumn(0,"RACE_DAY"),4,2);
	var sDay = substr(dsRaceInfo.GetColumn(0,"RACE_DAY"),6,2);

	stRaceInfo.Text = "("+rdoMeetCd.Text +" "+cboStndYear.Value+"년 "+sMonth+"월 "+sDay+"일 "+cboTms.Value+"회차 "+cboDayOrd.Text+")";	

	grdVeriFstSDLDTSatlit.SetFocus();

}

/*-------------------------------------------------------------------------------------------------+
>>  combobox, radio 바뀔 때 이벤트
+-------------------------------------------------------------------------------------------------*/
function fcCboOnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{

	switch ( obj.ID ) {	
		case "cboStndYear" :	// 년도 바꿀 경우
			fcSearchTms();		
		break;
		case "rdoMeetCd" :	// 경륜 경정 바꿀 경우
			fcSearchTms();		
		break;
		case "cboTms" :		// 회차 바꿀 경우
			fcSearchDayOrd();		
		break;

		case "cboDayOrd" :	// 일차 바꿀 경우
			fcSearch();		
		break;
	}

}
