function fcInit(obj) {
    dsVeriSDLDTPT.ClearData();
    dsVeriSaltit.ClearData();    
    dsSDLDTSUM.ClearData();
}

/*-------------------------------------------------------------------------------------------------+
>>  조회 버튼 클릭 時 체크 처리
+-------------------------------------------------------------------------------------------------*/
function fcSearchCheck(obj) {
    fcInit(obj);    //기존 데이터셋 초기화

	return true;
}

/*-------------------------------------------------------------------------------------------------+
>>  조회 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
/*-------------------------------------------------------------------------------------------------+
>>  년도별 최근 회차
+-------------------------------------------------------------------------------------------------*/
function fcSearchTms(obj) {
    if ( !fcSearchCheck(obj) ) return;

	var sServiceName = "rsm1080-service:searchTms"; // 최근 경주 결과 날짜
    var sInDataSet   = "";
    var sOutDataSet  = "dsTms=dsTms ";

	dsTms.ClearData();
	fcd_AddParam("MEET_CD", rdoMeetCd.Value);		// 개최지
	fcd_AddParam("STND_YEAR", cboStndYear.Value);
	fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack",false);
}

/*-------------------------------------------------------------------------------------------------+
>>  년도별 최근 회차별 최근 일차
+-------------------------------------------------------------------------------------------------*/
function fcSearchDayOrd(obj) {
    if ( !fcSearchCheck(obj) ) return;

	var sServiceName = "rsm1080-service:searchDayOrd"; // 최근 경주 결과 날짜

    var sInDataSet   = "";
    var sOutDataSet  = "dsDayOrd=dsDayOrd ";

	dsDayOrd.ClearData();
	fcd_AddParam("MEET_CD", rdoMeetCd.Value);		// 개최지
	fcd_AddParam("STND_YEAR", cboStndYear.Value);
	fcd_AddParam("TMS", cboTms.Value);
	fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack",false);
}
/*-------------------------------------------------------------------------------------------------+
>>  조회 내용 검색 처리
+-------------------------------------------------------------------------------------------------*/

function fcSearch(obj) {
    if ( !fcSearchCheck(obj) ) return;

	var sServiceName = "rsm1080-service:searchVeriFstSDLDTPT";
    var sInDataSet   = "";
    var sOutDataSet  = "dsVeriSDLDTPT=dsVeriSDLDTPT ";
        sOutDataSet += "dsVeriMyCat=dsVeriMyCat ";    
        sOutDataSet += "dsVeriSaltit=dsVeriSaltit ";    
        //sOutDataSet += "dsSDLDTSUM=dsSDLDTSUM ";    
        //sOutDataSet += "dsRaceDayInfo=dsRaceDayInfo ";          

	fcd_AddParam("STND_YEAR", cboStndYear.Value); // 기준년도
	fcd_AddParam("MEET_CD", rdoMeetCd.Value);		// 개최지
	fcd_AddParam("TMS", cboTms.Value);				// 회차
	fcd_AddParam("DAY_ORD", cboDayOrd.Value);		// 일차
	fcd_AddParam("RACE_DAY", dsDayOrd.GetColumn(dsDayOrd.FindRow("DAY_ORD", cboDayOrd.Value), "RACE_DAY"));

	fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack",true);

}



