/***************************************************************************************************
*                                      사용자 정의  처리 부분                                      *
***************************************************************************************************/
/*-------------------------------------------------------------------------------------------------+
>>  Transaction 후 처리 해야 할 내용!
+-------------------------------------------------------------------------------------------------*/
function fcCallBack(sServiceName, ErrorCode, ErrorMSG) {
	var nMycatAmt = 0;
    var nMySatlitAmt = 0;
    var nMycatAmtReal = 0;
    var tRow = 0;
    if ( !fnc_result(ErrorCode, ErrorMSG) ) return;
    if ( sServiceName == "rsm1080-service:searchVeriFstSDLDTPT" ) { // TMS 값 검색
		dsVeriSDLDTPT.fireEvent = false;
		//Trace(dsVeriSDLDTPT.savexml());
		//Trace(dsVeriMyCat.savexml());
		for(var iCnt=0;iCnt<dsVeriMyCat.RowCount();iCnt++) {
			tRow = dsVeriSDLDTPT.findrow("MEET_RACE_NO", dsVeriMyCat.GetColumn(iCnt, "MEET_RACE_NO"));
			if (tRow >= 0) {
				nMycatAmt = dsVeriSDLDTPT.GetColumn(tRow, "MYCAT_DIV");	
				nMycatAmtReal = dsVeriMyCat.GetColumn(iCnt, "MYCAT_AMT") - dsVeriMyCat.GetColumn(iCnt, "MYCAT_REFUND");
				if (nMycatAmt <> nMycatAmt) {
					fcSetRowTF(tRow);
				}
				dsVeriSDLDTPT.SetColumn(tRow, "MYCAT_REAL_AMT", nMycatAmtReal);
				dsVeriSDLDTPT.SetColumn(tRow, "MYCAT_REAL_REFUND", dsVeriMyCat.GetColumn(iCnt, "MYCAT_REFUND"));
			} else {
				fcSetRowTF(tRow);
			}
		}
		//Trace(dsVeriSaltit.savexml());
		var chocha_amt = 0;
		for(var iCnt=0;iCnt<dsVeriSaltit.RowCount();iCnt++) {
			tRow = dsVeriSDLDTPT.findrow("MEET_RACE_NO", dsVeriSaltit.GetColumn(iCnt, "MEET_RACE_NO"));
			if (tRow >= 0) {
				nMySatlitAmt  = dsVeriSDLDTPT.GetColumn(tRow, "MYCAT_DIV");	
				nMycatAmtReal = dsVeriSaltit.GetColumn(iCnt, "SATLIT_AMT_GREEN");
				if (nMycatAmt <> nMycatAmt) {
						fcSetRowTF(tRow);
				}
				dsVeriSDLDTPT.SetColumn(tRow, "SATLIT_AMT_GREEN", nMycatAmtReal);
				chocha_amt = dsVeriSDLDTPT.GetColumn(tRow, "POOL_TOTAL") - dsVeriSaltit.GetColumn(iCnt, "SATLIT_AMT");
				dsVeriSDLDTPT.SetColumn(tRow, "SATLIT_AMT_CHOCHA", chocha_amt);
				//Trace("chocha_amt="+chocha_amt);
			} else {
				fcSetRowTF(tRow);
			}
		}
		dsVeriSDLDTPT.row = -1;
		dsVeriSaltit.row = -1;
		dsSDLDTSUM.row = -1;
		dsVeriSDLDTPT.fireEvent = true;
		return;
	}
    if ( sServiceName == "rsm1080-service:searchTms" ) { // TMS 값 검색
		if(dsTms.GetRowCount()==0)
		{		
			dsTms.SetColumn(0,"TMS","");
			dsTms.SetColumn(0,"TMS_DESC","없음");
			cboTms.Index=0;
		}
		else
		{
			if(TMS != "")
			{
				cboTms.Value = TMS;
				TMS="";
			}
			else
			{		
				cboTms.Value = dsTms.GetColumn(0,"MAX_TMS");		
			}
		}
		fcSearchDayOrd();
		return;
	}
    if ( sServiceName == "rsm1080-service:searchDayOrd" ) {		
		if(dsDayOrd.GetRowCount()==0)
		{
			dsDayOrd.AddRow();
			dsDayOrd.SetColumn(0,"DAY_ORD","");
			dsDayOrd.SetColumn(0,"DAY_DESC","없음");
			cboDayOrd.Index=0;
		}
		else
		{
			if(DAY_ORD != "")
			{
				cboDayOrd.Value = DAY_ORD;
				DAY_ORD="";
			}
			else
			{
				cboDayOrd.Value = dsDayOrd.GetColumn(0,"MAX_DAY_ORD");
			}
		}
		fcSearch();
		return;
	}

	if ( sServiceName == "rsm1040-service:searchVeriFstSDLPTPayoff" )
	{
	}
	grdVeriSDLDTPT.SetFocus();

	//stRaceInfo.Text = "("+dsRaceDayInfo.GetColumn(0,"GUBUN_DESC") +" "+dsRaceDayInfo.GetColumn(0,"STND_YEAR")+"년 "+dsRaceDayInfo.GetColumn(0,"RACE_MM")+"월 "+dsRaceDayInfo.GetColumn(0,"RACE_DD")+"일 "+dsRaceDayInfo.GetColumn(0,"TMS")+"회차 "+dsRaceDayInfo.GetColumn(0,"DAY_ORD")+" 일차)";

}

/*-------------------------------------------------------------------------------------------------+
>>  상태를 불일치로 변경
+-------------------------------------------------------------------------------------------------*/
function fcSetRowTF(tRow)
{
	dsVeriSDLDTPT.SetColumn(tRow, "TF", "002");
	dsVeriSDLDTPT.SetColumn(tRow, "TF_DESC", "불일치");
}
/*-------------------------------------------------------------------------------------------------+
>>  combobox, radio 바뀔 때 이벤트
+-------------------------------------------------------------------------------------------------*/
function fcCboOnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
	switch ( obj.ID ) {	
		case "cboStndYear" :	// 년도 바꿀 경우
			fcSearchTms();		
		break;
		case "rdoMeetCd" :	// 경륜 경정 바꿀 경우
			fcSearchTms();		
		break;
		case "cboTms" :		// 회차 바꿀 경우
			fcSearchDayOrd();		
		break;

		case "cboDayOrd" :	// 일차 바꿀 경우
			fcSearch();		
		break;
	}
}


function fcOnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
	switch ( obj.ID ) {

		case "rdoAutoOPT" : // 수동 자동 구분
			if(strCode=="2")
			{
				cboTimeBox.Enable = true;
				SetTimer(1, parseInt(cboTimeBox.Value) *60 * 1000 ); // Timer구동
			}
			else
			{
				cboTimeBox.Enable = false;
				KillTimer(1); // Timer해제
			}

			break;

		case "cboTimeBox":
			KillTimer(1); // Timer해제
			SetTimer(1, parseInt(strCode) *60 * 1000 ); // Timer구동	
			break;
	}

}

function fcOnTimer(obj,nEventID)
{
	if(rdoAutoOPT.Value=="2")
	{
		fcSearch();
	}
}
