+-------------------------------------------------------------------------------------------------*/
function fcSearchTms(obj) {
    if ( !fcSearchCheck(obj) ) return;

	var sServiceName = "rsm2020-service:searchTms"; // ÃÖ±Ù °æÁÖ °á°ú ³¯Â¥
    var sInDataSet   = "";
    var sOutDataSet  = "dsTms=dsTms ";

	dsTms.ClearData();
	fcd_AddParam("MEET_CD",   "003");
	fcd_AddParam("STND_YEAR", cboStndYear.Value);

	fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack",false);
}

/*-------------------------------------------------------------------------------------------------+
>>  °æÁ¤ ³âµµº° °¡±î¿î È¸Â÷º° ÀÏÂ÷
+-------------------------------------------------------------------------------------------------*/
function fcSearchDayOrd(obj) {
    if ( !fcSearchCheck(obj) ) return;

	var sServiceName = "rsm2020-service:searchDayOrd"; // ÃÖ±Ù °æÁÖ °á°ú ³¯Â¥

    var sInDataSet   = "";
    var sOutDataSet  = "dsDayOrd=dsDayOrd ";

	dsDayOrd.ClearData();
	fcd_AddParam("MEET_CD",   "003");
	fcd_AddParam("STND_YEAR", cboStndYear.Value);
	fcd_AddParam("TMS", cboTms.Value);
	fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack",false);

}


/*-------------------------------------------------------------------------------------------------+
>>  Á¶È¸ ¹öÆ° Å¬¸¯ ãÁ Ã³¸®
+-------------------------------------------------------------------------------------------------*/
function fcSearch(obj) {
	if (tab0.TabIndex == 2) {
		fcSearchTueSales();
		return;
	}	
	fcSearchPrevTms();

    if ( !fcSearchCheck(obj) ) return;

	var sServiceName = "rsm2020-service:searchRaceDayInfo"; // ÃÖ±Ù °æÁÖ °á°ú ³¯Â¥

	if(RACE_DAY == fcd_GetSysdate("YYYYMMDD"))
	{
		sServiceName = "rsm2020-service:searchRaceDayInfoToday"; // ÃÖ±Ù °æÁÖ °á°ú ³¯Â¥
	}

    var sInDataSet   = "";
    var sOutDataSet  = "dsRaceDayInfo=dsRaceDayInfo ";    	// Àü³â´ëºñ ºñ±³ ds
		sOutDataSet  += "dsBFYYCompare=dsBFYYCompare ";    	// Àü³â´ëºñ ºñ±³ ds
		sOutDataSet  += "dsRaceSales=dsRaceSales ";    	// °æÁÖº° ¸ÅÃâÇöÈ²
		sOutDataSet  += "dsEntPrsnNum=dsEntPrsnNum ";    	// ÀÔÀåÀÎ¿ø 1 ds
		sOutDataSet  += "dsStayMana2=dsStayMana2 ";    	// ÀÔÀåÀÎ¿ø 2 ds
		sOutDataSet  += "dsOutSales=dsOutSales ";    	// Àå¿Ü ¸ÅÃâ ÇöÈ² ºñ±³ ds
		sOutDataSet  += "dsOutBFSales=dsOutBFSales ";    	// Àå¿Ü ÀüÈ¸ ¸ÅÃâ ÇöÈ² ºñ±³ ds
		sOutDataSet  += "dsOutEntPrsnNum=dsOutEntPrsnNum ";    	// Àå¿Ü ÀÔÀå ÀÎ¿ø ÇöÈ² ds
		sOutDataSet  += "dsSalesGoal=dsSalesGoal ";    	// ¸ÅÃâ¸ñÇ¥´ëºñ ds
		sOutDataSet  += "dsETC=dsETC ";    	// ±âÅ¸ Æ¯ÀÌ»çÇ× ds

	fcd_AddParam("STND_YEAR", cboStndYear.Value);
	fcd_AddParam("MEET_CD",   "003");
	fcd_AddParam("TMS",       cboTms.Value);
	fcd_AddParam("DAY_ORD",   cboDayOrd.Value);		


	// ¿äÀÏºñ±³
	var sRaceYoil = "";
	var nRow = dsDayOrd.FindRow("DAY_ORD", cboDayOrd.Value);
	if (nRow > -1) sRaceYoil = dsDayOrd.GetColumn(nRow, "RACE_YOIL");
	if (sRaceYoil != "") fcd_AddParam("RACE_YOIL", sRaceYoil);		

	fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", true);

	// ¸ÅÃâºñ±³ÀÚ·á Á¶È¸
	var sServiceName = "rsm2010-service:searchBeforeCompare";  // Grid Á¶È¸
	var sInDataSet   = "";
	var sOutDataSet  = "dsBeforeCompare=dsBeforeCompare ";     

	if( RACE_DAY == fcd_GetSysdate("YYYYMMDD"))
	{
		sServiceName = "rsm2010-service:searchBeforeCompareToday";  // Grid Á¶È¸
	}
	fcd_AddParam("RACE_DAY", RACE_DAY);        
	fcd_AddParam("MEET_CD", "003");        
	fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", true);

}

