/*-------------------------------------------------------------------------------------------------+
>>  Transaction ÈÄ Ã³¸® ÇØ¾ß ÇÒ ³»¿ë!
+-------------------------------------------------------------------------------------------------*/
function fcCallBack(sServiceName, ErrorCode, ErrorMSG) {

    if ( !fnc_result(ErrorCode, ErrorMSG) ) return;

    if ( sServiceName == "rsm2020-service:searchTms" ) { // TMS °ª °Ë»ö

		if(dsTms.GetRowCount()==0)
		{		
			dsTms.SetColumn(0,"TMS","");
			dsTms.SetColumn(0,"TMS_DESC","¾øÀ½");
			cboTms.Index=0;
		}
		else
		{
			cboTms.Value = dsTms.GetColumn(0,"MAX_TMS");		
		}
		fcSearchDayOrd();
		return;
	}
    if ( sServiceName == "rsm2020-service:searchDayOrd" ) {		

		if(dsDayOrd.GetRowCount()==0)
		{
			dsDayOrd.AddRow();
			dsDayOrd.SetColumn(0,"DAY_ORD","");
			dsDayOrd.SetColumn(0,"DAY_DESC","¾øÀ½");
			cboDayOrd.Index=0;
		}
		else
		{
			cboDayOrd.Value = dsDayOrd.GetColumn(0,"MAX_DAY_ORD");
			RACE_DAY		= dsDayOrd.GetColumn(0,"RACE_DAY"); // °æÁÖÀÏ
		}
		if (dsDayOrd.rowcount() > 2) tab0.tab1.stCompare.visible = true;
		else tab0.tab1.stCompare.visible = false;

		fcSearch();
		return;
	}

    if(sServiceName == "rsm2020-service:searchRaceDayInfo" || sServiceName == "rsm2020-service:searchRaceDayInfoToday") {
		if (dsOutSales.rowcount() > 0) {	// Àå¿ÜÁöÁ¡ ¸ÅÃâÀÚ·á°¡ Á¸ÀçÇÏ´Â °æ¿ì
			if (dsOutSales.GetColumn(dsOutSales.RowCount() -1,  "DIV_01_07_00") > 0) {
				Tab0.tab2.grdOutSales.SetColProp(nShareBonJangPos, "width", 70);
				var sShareNm = "¹Ì»ç¸®";
				if (dsRaceDayInfo.GetColumn(0, "MEET_CD") == "003") sShareNm = "±¤¸í";
				Tab0.tab2.grdOutSales.SetCellProp("head", nShareBonJangPos, "text", sShareNm);				
			} else {
				Tab0.tab2.grdOutSales.SetColProp(nShareBonJangPos, "width", 0);
			}
			//¿Ã¸²ÇÈ ÁöÁ¡ÀÇ ÀÚ·á°¡ ¾øÀ¸¸é ¾Èº¸ÀÌ°Ô Ã³¸®
			if (dsOutSales.GetColumn(dsOutSales.RowCount() -1,  "DIV_01_07_17") > 0) {
				Tab0.tab2.grdOutSales.SetColProp(12, "width", 70);
			} else {
				Tab0.tab2.grdOutSales.SetColProp(12, "width", 0);
			}

			// ¿äÀÏº°·Î ºñ±³ ¾È³» Ç¥½Ã
			if (dsRaceSales.GetColumn(0, "B_DIV_TOTAL")	== 0) tab0.tab1.stCompare.visible = true;
			else if (dsRaceSales.GetColumn(0, "YB_DIV_TOTAL") == 0) tab0.tab1.stCompare.visible = true;
			else tab0.tab1.stCompare.visible = false;

		}
		if (dsOutEntPrsnNum.rowcount() > 0) {
			var arrCellPos = [5, 7, 10, 18];
			var arrCellCol = ["INWON_15","INWON_18","INWON_21","INWON_00"];
			for (var i=0;i<arrCellPos.length;i++) {

				if (toNumber(dsOutEntPrsnNum.GetColumn(0, arrCellCol[i])) > 0) {
					tab0.tab2.grdOutEntPrsnNum.SetColProp(arrCellPos[i], "width", 80);
				} else {
					tab0.tab2.grdOutEntPrsnNum.SetColProp(arrCellPos[i], "width", 0);
				}
			}
		}

	} else if(sServiceName == "rsm2010-service:searchBeforeCompare" ) {
		//Trace(dsBeforeCompare.savexml());
	} else if(sServiceName == "rsm2020-service:searchPrevTms" ) {
		var sTemp = "("+dsPrevTms.GetColumn(0, "TMS")+"È¸)";
		tab0.tab1.grdRaceSales.SetCellProp("head", 6, "text", "ÀüÈ¸´ëºñ"+sTemp);
		sTemp = "("+dsPrevTms.GetColumn(1, "STND_YEAR")+"³â "+dsPrevTms.GetColumn(1, "TMS")+"È¸)";
		tab0.tab1.grdRaceSales.SetCellProp("head", 7, "text", "Àü³âµ¿È¸´ëºñ"+sTemp);

		if (toNumber(cboTms.Value) -1 != toNumber(dsPrevTms.GetColumn(0, "TMS"))) 
			tab0.tab1.grdRaceSales.SetCellProp("head", 6, "color", "mediumblue");
		else
			tab0.tab1.grdRaceSales.SetCellProp("head", 6, "color", "default");

		if (toNumber(cboStndYear.Value) -1 != toNumber(dsPrevTms.GetColumn(1, "STND_YEAR"))) 
			tab0.tab1.grdRaceSales.SetCellProp("head", 7, "color", "mediumblue");
		else
			tab0.tab1.grdRaceSales.SetCellProp("head", 7, "color", "default");
	}



	//Tab0.tab1.grdBFYYCompare.SetFocus();		
	tab0.tab1.stRaceDayInfo.Text = "°æÁ¤ ¸ÅÃâÇöÈ² "+dsRaceDayInfo.GetColumn(0,"RACE_DAY_INFO");

	// ÀüÈ¸ ¸ÅÃâ ÇöÈ² summary µ¥ÀÌÅÍ »ý¼º
	fcGrdOutSaleSummary();	

}

// ÀüÈ¸ ¸ÅÃâ ÇöÈ² summary µ¥ÀÌÅÍ »ý¼º
function fcGrdOutSaleSummary() 
{
