/*-------------------------------------------------------------------------------------------------+
>>  combobox, radio ¹Ù²ð ¶§ ÀÌº¥Æ®
+-------------------------------------------------------------------------------------------------*/
function fcCboOnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{


	switch ( obj.ID ) {

		case "cboStndYear" : // ±âÁØ³âµµ º¯°æ
			fcSearchTms();
			break;

		case "cboTms" :		// È¸Â÷ º¯°æ
			fcSearchDayOrd();		
			break;

		case "cboDayOrd" :	// ÀÏÂ÷ º¯°æ
			fcSearch();
			break;

		case "rdoAutoOPT" : // ¼öµ¿ ÀÚµ¿ ±¸ºÐ
			if(strCode=="2")
			{
				cboTimeBox.Enable = true;
				SetTimer(1, parseInt(cboTimeBox.Value) *60 * 1000 ); // Timer±¸µ¿
			}
			else
			{
				cboTimeBox.Enable = false;
				KillTimer(1); // TimerÇØÁ¦
			}

			break;

		case "cboTimeBox":
			KillTimer(1); // TimerÇØÁ¦
			SetTimer(1, parseInt(strCode) *60 * 1000 ); // Timer±¸µ¿	
			break;
	}


}




