function fcBtnOnClick(obj,nX,nY)
{
	switch ( obj.id )
    {
		case "btnFullScreen" :
			var sFormId 	= "frm_rsm::RSM2020P.xml";
			var sArgs 		= "";	
			var nCXScreen	= GetDeviceInfo("CXScreen");			
			var nCYScreen	= GetDeviceInfo("CYScreen");

			var strParam = "STND_YEAR="+cboStndYear.Value;
			strParam +=" TMS="+cboTms.Value;
			strParam +=" DAY_ORD="+cboDayOrd.Value;
			strParam +=" RACE_DAY="+RACE_DAY;

			GBL_RET_VALUE=strParam; // °ª »ý¼º	

			Dialog(sFormId, GBL_RET_VALUE,nCXScreen,nCYScreen, "Title=true CloseFlag=true","1","1");

		break;
	}
}


// Æ¯Á¤ÄÞÆ÷³ÍÆ®(±×¸®µå) ¸®»çÀÌÁî
function fcSetResize(obj,nX)
{	// ÇØ´çÈ­¸éÀº ±âÁØ»çÀÌÁîº¸´Ù Height°¡ Å©±â¶§¹®¿¡ ½ºÅ©·Ñ¹Ù »ý¼º¹ß»ý.±×¿¡ µû¶ó ±×¸®µå»çÀÌÁî Á¶Á¤
	var arrObj = array(-1);
	arrObj[0] = "Tab0.tab1.grdBFYYCompare";
	arrObj[1] = "Tab0.tab1.grdRaceSales";
	arrObj[2] = "Tab0.tab1.grdEntPrsnNum";
	arrObj[3] = "Tab0.tab1.grdStayMana2";
	arrObj[4] = "Tab0.tab2.grdOutSales";
	arrObj[5] = "Tab0.tab2.grdOutEntPrsnNum";
	arrObj[6] = "Tab0.tab1.grdBeforeCompare";	
	arrObj[7] = "Tab0.tab3.grdTueSales";
	arrObj[8] = "Tab0.tab1.grdSalesGoal";

	var nWidth = nX-28;
	if( nX <= 820){
		for( var i=0; i<length(arrObj); i++){
			object(arrObj[i]).Autofit = false;		
			object(arrObj[i]).right = 788;
			object(arrObj[i]).Redraw;
		}
		Tab0.Width = 800;
		Shape1.Width = 780;	
		divBtnLst.Left = this.width - 405;			
		Tab0.tab1.Static0.Left = 692;	//702;
		Tab0.tab1.Static1.Left = 692;	//7702;
		Tab0.tab1.Static3.Left = 692;	//7702;
		Tab0.tab2.Static2.Left = 692;	//7702;
		Tab0.tab2.Static5.Left = 692;	//7702;
		Tab0.tab3.stUnit.Left  = 692;	//7702;
		Tab0.tab1.taEtc.Right  = 780;
		Tab0.tab1.grdBeforeCompare.AutoFit		= false;
		Tab0.tab1.grdBeforeCompare.Right			= 788;
		tab0.tab1.stCompare.left = tab0.tab1.grdBeforeCompare.width - tab0.tab1.stCompare.width;
		return;
	}else{

		Tab0.Right = nWidth+10;

		Tab0.tab1.grdBFYYCompare.Right = nWidth;
		if( nX < 1350) Tab0.tab1.grdBFYYCompare.AutoFit = false;
		else  Tab0.tab1.grdBFYYCompare.AutoFit = true;

		if( nX < 788 ) Tab0.tab1.grdBFYYCompare.AutoFit = false;
		else Tab0.tab1.grdBFYYCompare.AutoFit = true;

		Tab0.tab1.grdRaceSales.Right = nWidth;
		if( nX < 1350 ) Tab0.tab1.grdRaceSales.AutoFit = false;
		else Tab0.tab1.grdRaceSales.AutoFit = true;


		Tab0.tab1.grdEntPrsnNum.Right = nWidth;
		if( nX < 788 ) Tab0.tab1.grdEntPrsnNum.AutoFit = false;
		else Tab0.tab1.grdEntPrsnNum.AutoFit = true;

		Tab0.tab1.grdSalesGoal.Right = nWidth;
		if( nX < 788 ) Tab0.tab1.grdSalesGoal.AutoFit = false;
		else Tab0.tab1.grdSalesGoal.AutoFit = true;

		Tab0.tab1.grdStayMana2.Right = nWidth;
		if( nX < 788 ) gTab0.tab1.rdStayMana2.AutoFit = false;
		else Tab0.tab1.grdStayMana2.AutoFit = true;

		Tab0.tab2.grdOutSales.Right = nWidth;
		if( nX < 1320 ) Tab0.tab2.grdOutSales.AutoFit = false;
		else Tab0.tab2.grdOutSales.AutoFit = true;

		Tab0.tab2.grdOutEntPrsnNum.Right = nWidth;
		if( nX < 1320 ) Tab0.tab2.grdOutEntPrsnNum.AutoFit = false;
		else Tab0.tab2.grdOutEntPrsnNum.AutoFit = true;

		Tab0.tab1.grdBeforeCompare.Right = nWidth;
		if( nX < 831 ) Tab0.tab1.grdBeforeCompare.AutoFit = false;
		else Tab0.tab1.grdBeforeCompare.AutoFit = true;

		Tab0.tab3.grdTueSales.Right = nWidth;

	}

	// °øÅë
	Shape1.Width = nWidth-8;
	divBtnLst.Left = this.width - 405;	
	Tab0.tab1.Static0.Left = this.width - 121;
	Tab0.tab1.Static1.Left = this.width - 121;
	Tab0.tab2.Static2.Left = this.width - 121;
	Tab0.tab1.Static3.Left = this.width - 121;
	Tab0.tab2.Static5.Left = this.width - 121;
	Tab0.tab3.stUnit.Left  = this.width - 121;

	Tab0.tab1.taEtc.Right  = nWidth;

	tab0.tab1.stCompare.left = tab0.tab1.grdBeforeCompare.width - tab0.tab1.stCompare.width;
}


