{
	fcSetResize(obj,nCx);
}

// °æÁÖº° ÇöÈ² »ó¼¼ º¸±â
function fcOnCellClick(obj,nRow,nCell,nX,nY,nPivotIndex)
{	
	switch ( obj.id )
    {
		case "grdRaceResult" :
			if(nCell=='0')
			{
				var sFormId 	= "frm_rsm::RSM2031P.xml";
				var sArgs 		= "";		

				var sParam = " MEET_CD="+dsRaceResult.GetColumn(nRow,"MEET_CD");
				sParam += " STND_YEAR="+cboStndYear.Value;
				sParam += " TMS="+dsRaceResult.GetColumn(nRow,"TMS");
				sParam += " DAY_ORD="+dsRaceResult.GetColumn(nRow,"DAY_ORD");
				sParam += " RACE_DAY="+RACE_DAY;
				sParam += " RACE_NO="+dsRaceResult.GetColumn(nRow,"RACE_NO");

				GBL_RET_VALUE=sParam; // °ª »ý¼º	

				Dialog(sFormId, GBL_RET_VALUE,704,344, "Title=true CloseFlag=true","1","1");
			}
		break;
	}

}

// ÁöÁ¡º° ¸ÅÃâ ÇöÈ²
function fcBrncSales()
{
	// Ã¹¹øÂ° Çà	
	dsBrncSalesTmp.SetColumn(1,"BONGJANG",dsBrncSales.GetColumn(0,"BONGJANG"));	// º»Àå
	dsBrncSalesTmp.SetColumn(1,"JANG11",dsBrncSales.GetColumn(0,"JANG11"));		// ¼ö¿ø
	dsBrncSalesTmp.SetColumn(1,"JANG12",dsBrncSales.GetColumn(0,"JANG12"));		// »óºÀ
	dsBrncSalesTmp.SetColumn(1,"JANG13",dsBrncSales.GetColumn(0,"JANG13"));		// ÀÏ»ê
	dsBrncSalesTmp.SetColumn(1,"JANG14",dsBrncSales.GetColumn(0,"JANG14"));		// ºÐ´ç
	dsBrncSalesTmp.SetColumn(1,"JANG15",dsBrncSales.GetColumn(0,"JANG15"));		// µ¿´ë¹®
