function fcSearchAll(num) {
	var sServiceName = "";
    var sInDataSet   = "";
    var sOutDataSet  = "";	

	switch (num) {
		case 0 :
			sServiceName = "rsm3050-service:searchDIVTMS01";
			sOutDataSet  = "dsDIVTMS=dsDIVTMS ";	
			break;
		case 1 : 	//¿ùº° ¸ÅÃâ¾×
			sServiceName = "rsm3050-service:searchDIVMONTH01";
			sOutDataSet  = "dsDivMonths=dsDivMonths ";	
			break;
		case 2 :
			sServiceName = "rsm3050-service:searchDIVTMS02";
			sOutDataSet  = "dsDIVTMSGsum=dsDIVTMSGsum ";	
			break;
		case 3 : 	//¿ùº° ¸ÅÃâ¾×
			sServiceName = "rsm3050-service:searchDIVMONTH02";
			sOutDataSet  = "dsDivMonthsGsum=dsDivMonthsGsum ";	
			break;
	} 
	fcd_AddParam("MEET_CD", rdoMeetCd.Value);
	fcd_AddParam("RACE_DAY_ST", calRaceDaySt.Value);
	fcd_AddParam("RACE_DAY_END", calRaceDayEnd.Value);	

	fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", true);	
}

