/*-------------------------------------------------------------------------------------------------+
>>  combobox, radio ¹Ù²ð ¶§ ÀÌº¥Æ®
+-------------------------------------------------------------------------------------------------*/
function fcOnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
	var headTitle = "¹Ì»ç¸®";
	if (strtext == "°æÁ¤") headTitle = "±¤¸í";

	Tab0.tab1.grdDIVTMS.SetCellProp("head",        22, "text", headTitle);
	Tab0.tab1.grdDIVTMS.SetCellProp("head",        44, "text", headTitle);
	Tab0.tab2.grdDivMonths.SetCellProp("head",     22, "text", headTitle);
	Tab0.tab2.grdDivMonths.SetCellProp("head",     44, "text", headTitle);
	Tab0.tab3.grdDIVTMSGsum.SetCellProp("head",    22, "text", headTitle);
	Tab0.tab4.grdDivMonthsGsum.SetCellProp("head", 22, "text", headTitle);

	fcSearch();
}

function fcOnChar(obj,strPreText,nChar,strPostText,nLLParam,nHLParam)
{
	switch ( obj.ID ) {		
		// °æÁÖ °Ë»ö ½ÃÀÛ ÀÏÀÚ¿¡¼­ ENTER_KEY ´­·¶À» °æ¿ì
		case "calRaceDaySt" :	
			if ( nChar == ENTER_KEY ) {
				fcSearch();
				return;
			}
		break;
		// °æÁÖ °Ë»ö ¸¶Áö¸· ÀÏÀÚ¿¡¼­ ENTER_KEY ´­·¶À» °æ¿ì
		case "calRaceDayEnd" :	
			if ( nChar == ENTER_KEY ) {
				fcSearch();
				return;
			}
		break;

	}
}

// Æ¯Á¤ÄÞÆ÷³ÍÆ®(±×¸®µå) ¸®»çÀÌÁî
function fcSetResize(obj,nX,nY)
{	
	var arrObj = array(-1);

	var nWidth = nX-10;
	if( nX <= 820){

		tab0.Width = 806;
		tab0.tab1.grdDIVTMS.Autofit = false;			
		tab0.tab1.grdDIVTMS.width = 800;
		tab0.tab2.grdDivMonths.Autofit = false;			
		tab0.tab2.grdDivMonths.width = 800;
		tab0.tab3.grdDIVTMSGsum.Autofit = false;			
		tab0.tab3.grdDIVTMSGsum.width = 800;
		tab0.tab4.grdDivMonthsGsum.Autofit = false;			
		tab0.tab4.grdDivMonthsGsum.width = 800;

		Shape1.Width 	= 801;	
		divBtnLst.Left 	= this.width - 393;			
		Static0.Left 	= this.width - 91;
		return;
	}

	tab0.tab1.grdDIVTMS.right = nWidth - 10;
	tab0.tab2.grdDivMonths.right = nWidth - 10;
	tab0.tab3.grdDIVTMSGsum.right = nWidth - 10;
	tab0.tab4.grdDivMonthsGsum.right = nWidth - 10;
	tab0.Right = nWidth;

	if( nX > 2400 )	{
		tab0.tab1.grdDIVTMS.Autofit = true;		
		tab0.tab2.grdDivMonths.Autofit = true;		
		tab0.tab3.grdDIVTMSGsum.Autofit = true;		
		tab0.tab4.grdDivMonthsGsum.Autofit = true;		
	}else{
	 	tab0.tab1.grdDIVTMS.Autofit = false;	
	 	tab0.tab2.grdDivMonths.Autofit = false;	
	 	tab0.tab3.grdDIVTMSGsum.Autofit = false;	
	 	tab0.tab4.grdDivMonthsGsum.Autofit = false;	
	}

	// °øÅë
	Shape1.Width 	= nWidth-8;
	divBtnLst.Left 	= this.width - 393;	
	Static0.Left	= this.width - 91;

	var nHeight = nY;
	if( nY <= 580){
		tab0.tab1.grdDIVTMS.Height = 450;
		tab0.tab2.grdDivMonths.Height = 450;
		tab0.tab3.grdDIVTMSGsum.Height = 450;
		tab0.tab4.grdDivMonthsGsum.Height = 450;

		tab0.height = 475;
	} else {
		tab0.tab1.grdDIVTMS.Height = nHeight-tab0.tab1.grdDIVTMS.top-120;
		tab0.tab2.grdDivMonths.Height = nHeight-tab0.tab2.grdDivMonths.top - 120;
		tab0.tab3.grdDIVTMSGsum.Height = nHeight-tab0.tab3.grdDIVTMSGsum.top-120;
		tab0.tab4.grdDivMonthsGsum.Height = nHeight-tab0.tab4.grdDivMonthsGsum.top - 120;
		tab0.height = nHeight - tab0.top;
	}
}

// ÇØ´ç Æû»çÀÌÁî º¯°æ½Ã
function fcOnSize(obj,nCx,nCy,nState)
{	
	fcSetResize(obj,nCx,nCy);
}
