+-------------------------------------------------------------------------------------------------*/
function fcSearchCheck(obj) {
    fcInit(obj);    
    return true;
}

function fcSearch(obj) {
    if ( !fcSearchCheck(obj) ) return;

    var sServiceName = "rsm5010-service:searchPayCntnt";
    var sInDataSet   = "";
    var sOutDataSet  = "dsPcTax=dsPcTax ";     

    //조회조건
    fcd_AddParam("PAY_YEAR"     , cboPayYear.Value);
    fcd_AddParam("MEET_CD"      , cboMeetCdSearch.Value);
    fcd_AddParam("SELL_CD"      , cboSellCdSearch.Value);
    // 광명에 해당되는 지점코드는 여러개이므로 정규식을 사용하여 조회한다.
    if( cboDivNoSearch.Value == "00"){
		fcd_AddParam("BRNC_CD"      , "^0[1-9]"); // 정규식표현		
    }else{
		fcd_AddParam("BRNC_CD"      , cboDivNoSearch.Value);		
    }

    fcd_AddParam("SELL_CD1"     , "02");
    fcd_AddParam("SELL_CD2"     , "04");

    fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", true, "S");    
}

/*-------------------------------------------------------------------------------------------------+
>>  저장 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/

// 저장 전에 체크
function fcSaveCheck(obj) {  
    //변경된 자료가 있는지 체크
    if ( !fnc_DatasetChangeCheck(dsPcTax) ) {
        //저장 할 자료가 없음
        fnc_Message(SNIS_COM_1006, "N");    
        return false;
    }
    else {
		var tmpResNo;
		for( var i=0; i < dsPcTax.rowcount() ; i++) {
			if ( length(trim(dsPctax.GetColumn(i,"FORE_NO"))) =0) { // 외국인/특이번호 가 없을때만 확인
				tmpResNo = Substr(dsPctax.GetColumn(i,"CUST_SSN"),0,6)+Substr(dsPctax.GetColumn(i,"CUST_SSN"),7,7);
				if(!fcResidenceNumberCheck(tmpResNo)) {
					fnc_Message(dsPcTax.getcolumn(i,"CUST_NM") + " 고객의 주민등록번호를 확인하십시오.\n주민번호가 특이한경우 '외국인번호/특이번호'에 입력해주시기바랍니다.", "N");    
					dsPcTax.row = i;
					return false;
				}
			}
		}		
    }
    return true;
}

function fcSave(obj) {

    if ( !fcSaveCheck() ) return;   

    if(fnc_Message(SNIS_COM_1012, "Y")) {           
        var sServiceName = "rsm5010-service:savePcTax";
        var sInDataSet   = "dsPcTax=dsPcTax:u ";
        var sOutDataSet  = "dsPcTax=dsPcTax ";    

        fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", false, "U");
    }
}
