/*-------------------------------------------------------------------------------------------------+
>>  Transaction 후 처리 해야 할 내용!
+-------------------------------------------------------------------------------------------------*/
function fcCallBack(sServiceName, ErrorCode, ErrorMSG) {
    if ( !fnc_result(ErrorCode, ErrorMSG) ) return;

    // 조회 완료 후
    if ( sServiceName == "rsm5010-service:searchPayCntnt" ) {
		fcBtnControl();
    }

    // 저장 완료 후
    if ( sServiceName == "rsm5010-service:savePayCntnt" ) {
		fnc_Message(SNIS_CRA_B999, "N","저장이 완료되었습니다!");
        fcSearch();     
    }

    // 확정 완료 후
    if ( sServiceName == "rsm5010-service:saveCfmCntnt" ) {
		fnc_Message(SNIS_CRA_B999, "N","확정이 완료되었습니다!");
        fcSearch();     
    }

    // 1차확정 취소
    if( sServiceName == "rsm5010-service:saveCfmCancel") {
		fnc_Message(SNIS_CRA_B999, "N","확정취소가 완료되었습니다!");
		fcSearch();
    }

    if( sServiceName == "rsm5010-service:savePcTaxPrint") {

		fcSearch();
    }

    if( sServiceName == "rsm5010-service:savePcTax") {

		fcSearch();
    }
}

// 확정/확정취소 컨트롤
function fcBtnControl()
{
	if( dsPcTax.rowcount > 0){	
		// THR_CFM_CD=='001' 3차확정 
		// SND_CFM_CD='001' 2차확정 
		// FST_CFM_CD='001' 1차확정
		var THR_CFM_CD = dsPcTax.GetColumn(0,"THR_CFM_CD");
		var SND_CFM_CD = dsPcTax.GetColumn(0,"SND_CFM_CD");
		var FST_CFM_CD = dsPcTax.GetColumn(0,"FST_CFM_CD");

		cfmBtn.Enable = iif(THR_CFM_CD=='001' , false , iif(SND_CFM_CD='001' , false , iif(FST_CFM_CD='001',false , true)));
		btnCfmCancel.Enable =iif(THR_CFM_CD=='001' , false , iif(SND_CFM_CD='001' , false , iif(FST_CFM_CD='001',true , false)));

	}else{
		cfmBtn.Enable = false;
		btnCfmCancel.Enable = false;
	}
}
