/*-------------------------------------------------------------------------------------------------+
>>  단축키 처리(Form KeyDown)
+-------------------------------------------------------------------------------------------------*/
function fcHotKey(obj,objSenderObj,nChar,bShift,bControl,bAlt,nLLParam,nHLParam) {

    //Return Key Press時 Next Component focus
    if ( nChar == ENTER_KEY ) {
        var oFocusComponent = GetNextComponent(true);
        oFocusComponent.setfocus();
        return;
    }
    //Control Key가 눌려져 있다면 단축키 처리
    //if (bControl) fnc_HotKey(this, nChar);
}


function fcBtnOnClick(obj) 
{       
	if(Length(cboDivNoSearch.Value) <=0 ){
		   fnc_Message(SNIS_COM_1035,"N","지점");
			return;
	}


	switch(obj.id) {

        case "cfmBtn":  //확정버튼
			dsPcTax.fireEvent = false;
			var nCnt = 0;
			dsPcTax.Filter("FST_CFM_CD=='001'||SND_CFM_CD=='001'||THR_CFM_CD=='001'");
			nCnt = dsPcTax.rowcount;
			dsPcTax.UnFilter();	
			dsPcTax.fireEvent = true;
			// 기 확정 건이 존재
			if( nCnt > 0){
				fnc_Message(SNIS_CRA_B999, "N","기 확정건이 존재하므로 확정 하실 수 없습니다!");
				return;
			}

            //변경된 자료가 있는지 체크 => 변경된 자료가 있으면 작업 중지
            if ( fnc_DatasetChangeCheck(dsPcTax) ) {
                fnc_Message(SNIS_COM_1052, "N");
                return;
            }

            if( dsPcTax.rowcount == 0 ) {
                fnc_Message(SNIS_COM_1053, "N");
                return;
            }

            //성명과 주소가 모든 행에 등록되었는지 Check 
            for( var i = 0; i < dsPcTax.rowcount ; i++) {
                if( trim(dsPcTax.GetColumn(i, "CUST_NM")).length == 0 ) {
                    fnc_Message(SNIS_RBM_D001, "N", i+1, "성명");
                    dsPcTax.row = i;
                    return;
                }

                if( trim(dsPcTax.GetColumn(i, "CUST_ADDR")).length == 0 ) {
                    fnc_Message(SNIS_RBM_D001, "N", i+1, "주소");
                    dsPcTax.row = i;
                    return;
                }
            }

            if(fnc_Message(SNIS_RBM_D003, "Y")) {           
                var sServiceName = "rsm5010-service:saveCfmCntnt";
                var sInDataSet   = "dsPcTax=dsPcTax:a ";
                var sOutDataSet  = "dsPcTax=dsPcTax ";    

                //서버로 넘길값
				fcd_AddParam("SEND_VALUE", "RSM5010");

                fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", false);
            }

            break;

		case "printBtn":  //확정버튼
			dsPcTaxPrint.CopyRow(0, "dsPcTax", dsPcTax.currow);

			sSubUrl   = "";                                             // 레포트 파일이 있는 서브디렉토리
			sRptNm    = "rsm/rsm5010.jrf";                                // 레포트 파일명
			sDataSet  = "dsPcTaxPrint=dsPcTaxPrint ";					//UBI 쿼리명, MIP Dataset명"
			sArg      = "";		
			sRptDrct  = 0;  

			fnc_RPTPrint(sSubUrl, sRptNm, sDataSet, sArg, sRptDrct);

			dsPcTax.SetColumn(dsPcTax.currow, "PRINT_DT" , ToDay());

			var sServiceName = "rsm5010-service:savePcTaxPrint";
			var sInDataSet   = "dsPcTax=dsPcTax:u ";
			var sOutDataSet  = "";    

			fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", false);
		break;
    }
}
// 확정취소
function btnCfmCancel_OnClick(obj)
{
	// 1차 이후 확정건이 존재하면 확정취소 불가판단
	dsPcTax.fireEvent = false;
	var nCnt = 0;
	dsPcTax.Filter("SND_CFM_CD=='001'||THR_CFM_CD=='001'");
	nCnt = dsPcTax.rowcount;
	dsPcTax.UnFilter();	

	dsPcTax.Filter("FST_CFM_CD=='001'");
	var nCnt2 = dsPcTax.rowcount;
	dsPcTax.UnFilter();
	dsPcTax.fireEvent = true;

	// 기 확정 건이 존재
	if( nCnt > 0){
		fnc_Message(SNIS_CRA_B999, "N","조회된 데이타 중 1차 이후 확정 건이 존재하므로 \n 확정취소 하실 수 없습니다!");		
		return;
	}

	// 확정 건이 존재는지 체크
	if( nCnt2 == 0){
		fnc_Message(SNIS_CRA_B999, "N","조회된 데이타 중 1차 이후 확정 건이 존재하지 \n 않으므로 확정취소 하실 수 없습니다!");		
		return;
	}


	if( !fnc_Message(SNIS_CRA_B999, "Y","확정취소를 하시겠습니까?")) return; 

	var sServiceName = "rsm5010-service:saveCfmCancel";
	var sInDataSet   = "dsPcTax=dsPcTax ";
	var sOutDataSet  = "";    
	fcd_AddParam("SEND_VALUE"	, "FST");	 // 1차 확정취소
	fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", false);

}

function fcDsOnRowPosChanged(obj,nOldRow,nRow)
{
	/*iif(THR_CFM_CD=='001' , '3차확정' ,
	 iif(SND_CFM_CD='001' , '2차확정' ,
	  iif(FST_CFM_CD='001','1차확정' , '미확정')))*/

	  if(dsPcTax.GetColumn(nRow,"THR_CFM_CD") == "001" 
				|| dsPcTax.GetColumn(nRow,"SND_CFM_CD") == "001" 
				|| dsPcTax.GetColumn(nRow,"FST_CFM_CD") == "001" ){

				edForeNo.Enable = false;
				edCustName.Enable = false;
				edCustAddr.Enable = false;

	  }else{
	  			edForeNo.Enable = true;
				edCustName.Enable = true;
				edCustAddr.Enable = true;

	  }

}

function fcCboOnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
	switch ( obj.id ) {
		case "cboDivNoSearch" :
			fcSearch();

			// 확정버튼 제어
			if (IsExistVar("sInpt", true)) {
				if (sInpt == "T") {
					if (cboDivNoSearch.Value <> gdsUser.GetColumn(0, "BRNC_CD")) {
						cfmBtn.Visible = false;
						btnCfmCancel.Visible = false;
					} else {
						cfmBtn.Visible = true;
						btnCfmCancel.Visible = true;
					}
				}
			}

			// 승인권한자면.. 다 열어주자  20150208 김일준
			if (IsExistVar("sApro", true)) {
				if (sInpt == "T") {
					cfmBtn.Visible = true;
					btnCfmCancel.Visible = true;
				}
			}
			break;

	}

}
