function fcInit(obj) {
    dsPcTax.ClearData();
}

/*-------------------------------------------------------------------------------------------------+
>>  조회 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcSearchCheck(obj) {
    fcInit(obj);    
    return true;
}

function fcSearch(obj) {
    if ( !fcSearchCheck(obj) ) return;

    var sServiceName = "rsm5010-service:searchPayCntnt";
    var sInDataSet   = "";
    var sOutDataSet  = "dsPcTax=dsPcTax ";     

    //조회조건
    fcd_AddParam("PAY_YEAR"     , cboPayYear.Value);
    fcd_AddParam("MEET_CD"      , cboMeetCdSearch.Value);
    fcd_AddParam("SELL_CD"      , cboSellCdSearch.Value);
    fcd_AddParam("SELL_CD1"     , "01");
    fcd_AddParam("SELL_CD2"     , "03");

    fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", true, "S");
}

/*-------------------------------------------------------------------------------------------------+
>>  저장 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/

// 저장 전에 체크
function fcSaveCheck(obj) {  
    //변경된 자료가 있는지 체크
    if ( !fnc_DatasetChangeCheck(dsPcTax) ) {
        //저장 할 자료가 없음
        fnc_Message(SNIS_COM_1006, "N");    
        return false;
    }

    // 필수항목체크
	oDataSet          = dsPcTax;
	sMandatoryColID   = "MEET_CD|SELL_CD|TSN|CUST_SSN|CUST_NM|CUST_ADDR|JIGEUP_DT|JIGEUP_AMT|COST|GITA1|GITA2|GITA_PAY";
	sMandatoryColName = "시행처|판매처|TSN|주민등록번호|성명|주소|지급일자|지급금액|필요경비|소득세|주민세|차인지금액";
	oGrid             = grdPcTax;

	if ( fnc_GetMandatoryViolateRow(oDataSet, sMandatoryColID, sMandatoryColName, oGrid) > -1 ) {
	    return false;
	}

    return true;
}

function fcSave(obj) {

    if ( !fcSaveCheck() ) return;   

    if(fnc_Message(SNIS_RBM_D004, "Y", cboPayYear.Value)) {           
        var sServiceName = "rsm5030-service:savePcTaxCross";
        var sInDataSet   = "dsPcTax=dsPcTax:u ";
        var sOutDataSet  = "dsPcTax=dsPcTax ";    

		//조회조건
		fcd_AddParam("PAY_YEAR"     , cboPayYear.Value);
		fcd_AddParam("MEET_CD"      , cboMeetCdSearch.Value);
		fcd_AddParam("SELL_CD"      , cboSellCdSearch.Value);
		fcd_AddParam("SELL_CD1"     , "01");
		fcd_AddParam("SELL_CD2"     , "03");


        fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", false, "U");
    }
}
