/*-------------------------------------------------------------------------------------------------+
>>  Transaction 후 처리 해야 할 내용!
+-------------------------------------------------------------------------------------------------*/
function fcCallBack(sServiceName, ErrorCode, ErrorMSG) {
    if ( !fnc_result(ErrorCode, ErrorMSG) ) return;

    // 조회 완료 후
    if ( sServiceName == "rsm5010-service:searchPayCntnt" ) {
        if( dsPcTax.rowcount > 0 ) {
			var nRow = dsCfmCd.FindRowNF("CD", dsPcTax.GetColumn(dsPcTax.currow, "FST_CFM_CD"));
			stResult.Text = dsPcTax.GetColumn(dsPcTax.currow, "PAY_YEAR") + "년 "
			                + dsCfmCd.GetColumn(nRow, "CD_NM");	
			 fcBtnControl(dsPcTax.GetColumn(dsPcTax.currow, "FST_CFM_CD"));               		
        } else {
			stResult.Text = "조회된 결과가 없습니다";
			fcBtnControl("x");
        }
    }

    // 저장 완료 후
    if ( sServiceName == "rsm5030-service:savePcTaxCross" ) {
        fcSearch();     
    }   

    // 확정 완료 후
    if ( sServiceName == "rsm5010-service:saveCfmCntnt" ) {
    	fnc_Message(SNIS_CRA_B999, "N","확정이 완료되었습니다!");
        fcSearch();     
    }

    // 확정 취소완료 후
    if( sServiceName == "rsm5010-service:saveCfmCancel") {
		fnc_Message(SNIS_CRA_B999, "N","확정취소가 완료되었습니다!");
		fcSearch();
    }

}

function fcBtnControl(strCfm)
{
	if( strCfm == "001"){
		// 확정
		cfmBtn.Enable = false;
		btnCfmCancel.Enable = true;
	}else if( strCfm == "x"){
		// 사용불가
		cfmBtn.Enable = false;
		btnCfmCancel.Enable = false;
	}else{
		// 미확정
		cfmBtn.Enable = true;
		btnCfmCancel.Enable = false;
	}
}
