/*-------------------------------------------------------------------------------------------------+
>>  단축키 처리(Form KeyDown)
+-------------------------------------------------------------------------------------------------*/
function fcHotKey(obj,objSenderObj,nChar,bShift,bControl,bAlt,nLLParam,nHLParam) {

    //Return Key Press時 Next Component focus
    if ( nChar == ENTER_KEY ) {
        var oFocusComponent = GetNextComponent(true);
        oFocusComponent.setfocus();
        return;
    }
    //Control Key가 눌려져 있다면 단축키 처리
    //if (bControl) fnc_HotKey(this, nChar);
}

function fcBtnOnClick(obj)
{
	switch( obj.id ) {
		case "excelBtn" :

			// 화일을 읽어들인다
			fdo.Type = "OPEN";	
			if ( !fdo.open() )	return;

			var v_Path 	= fdo.FilePath + "\\" + fdo.FileName;
			// 화일을 import한다
			var rtn = ext_ExcelImportByIndex(v_Path, 0, "dsExcel", 0);

			// 첫째 로우를 삭제한다(엑셀 헤더값)
			dsExcel.DeleteRow(0);


			dsPcTax.fireEvent = false;
			dsPcTax.ClearData();
			var nRow;
			for(var i=0; i< dsExcel.rowcount;i++){

				var sCheck     = "";		
				var sMeetCd    = dsExcel.GetColumn(i, "COL01");
				var sSellCd    = dsExcel.GetColumn(i, "COL02");
				var sTsn       = dsExcel.GetColumn(i, "COL03");
				var sCustSsn   = Replace(dsExcel.GetColumn(i, "COL04"),"-","");
				var sCustNm    = dsExcel.GetColumn(i, "COL05");
				var sCustAddr  = dsExcel.GetColumn(i, "COL06");
				var sJigupDt   = Mid(dsExcel.GetColumn(i, "COL07"),0,8);
				var sJigupAmt  = dsExcel.GetColumn(i, "COL08");
				var sCost      = dsExcel.GetColumn(i, "COL09");
				var sGita1     = dsExcel.GetColumn(i, "COL10");
				var sGita2     = dsExcel.GetColumn(i, "COL11");
				var sGitaPay   = dsExcel.GetColumn(i, "COL12");
				//var sRefunm    = dsExcel.GetColumn(i, "COL13");
				//var sSellAmt   = dsExcel.GetColumn(i, "COL14");	
				var sForeNo    = dsExcel.GetColumn(i, "COL15");
				var sPayYear   = substr(sJigupDt, 0, 4);

				if( sMeetCd == "1" || sMeetCd == "2" || sMeetCd == "3" || sMeetCd = "4" ) {
					sMeetCd = "00" + sMeetCd;
				} else {
					sMeetCd = "";
					sCheck  = "*";
				}

				if( sSellCd == "2" || sSellCd = "4" ) {
					sSellCd = "0" + sSellCd;
				} else {
					sSellCd = "";
					sCheck  = "*";
				}			
				if( sTsn.length != 14) {
					if (sTsn.length == 0) {
						sTsn = Right("000" + sMeetCd, 4)+"-"+Right("000" + sSellCd, 4)+"-"+Right("000"+toString(i+1),4);
					} else {
						sTsn = "";
						sCheck  = "*";
					}
				}

				if( !fnc_ResidenceNumberCheck(sCustSsn) ) {
					sCustSsn = "";
					sCheck  = "*";
				} else {
					sCustSsn = substr(sCustSsn, 0, 6) + "-" + substr(sCustSsn, 6, 13);
				}

				if( !fnc_IsDate(sJigupDt) ) {
					sJigupDt = "";
					sCheck  = "*";
				}

				if( !IsDigit(sJigupAmt) ) {
					sJigupAmt = "";
					sCheck  = "*";
				}

				if( !IsDigit(sCost) ) {
					sCost = "";
					sCheck  = "*";
				}

				if( !IsDigit(sGita1) ) {
					sGita1 = "";
					sCheck  = "*";
				}

				if( !IsDigit(sGita2) ) {
					sGita2 = "";
					sCheck  = "*";
				}

				if( !IsDigit(sGitaPay) ) {
					sGitaPay = "";
					sCheck  = "*";
				}

				/*
				if( !IsDigit(sRefunm) ) {
					sRefunm = "";
					sCheck  = "*";
				}

				if( !IsDigit(sSellAmt) ) {
					sSellAmt = "";
					sCheck  = "*";
				}
				*/

				if (cboPayYear.Value != sPayYear) {
					sJigupDt = "";
					sCheck  = "*";
				}

				nRow = dsPcTax.AddRow();

				dsPcTax.SetColumn(nRow, "MEET_CD"   , sMeetCd);
				dsPcTax.SetColumn(nRow, "SELL_CD"   , sSellCd);
				dsPcTax.SetColumn(nRow, "TSN"       , sTsn);
				dsPcTax.SetColumn(nRow, "CUST_SSN"  , sCustSsn);
				dsPcTax.SetColumn(nRow, "CUST_NM"   , sCustNm);
				dsPcTax.SetColumn(nRow, "CUST_ADDR" , sCustAddr);
				dsPcTax.SetColumn(nRow, "JIGEUP_DT" , sJigupDt);		
				dsPcTax.SetColumn(nRow, "PAY_YEAR"  , sPayYear);
				dsPcTax.SetColumn(nRow, "JIGEUP_AMT", sJigupAmt);
				dsPcTax.SetColumn(nRow, "COST"      , sCost);
				dsPcTax.SetColumn(nRow, "GITA1"     , sGita1);
				dsPcTax.SetColumn(nRow, "GITA2"     , sGita2);
				dsPcTax.SetColumn(nRow, "GITA_PAY"  , sGitaPay);
				//dsPcTax.SetColumn(nRow, "REFUND"    , sRefunm);
				//dsPcTax.SetColumn(nRow, "SELL_AMT"  , sSellAmt);		
				dsPcTax.SetColumn(nRow, "FORE_NO"   , sForeNo);
				dsPcTax.SetColumn(nRow, "ECHK"      , sCheck);	

			}	
			dsPcTax.fireEvent = true;
			dsExcel.fireEvent = true;

			// 확정 확정취소 사용불가	
			fcBtnControl("x");			
			break;

		case "downBtn" :			
			var sServerFileName = dsFile.Lookup("CD", "001", "CD_NM");
			if (sServerFileName == null) {
				fnc_message("다운받을 엑셀 파일 정보가 존재하지 않습니다.", "N");
				return;
			}
			var arrRtn  = split(sServerFileName, "/");
			var sFileNm = arrRtn[arrRtn.length-1];
			var sLocalFileName  = fcd_GetSelectFile("Save", sFileNm);	

			if ( sLocalFileName == null ) return;

			fnc_FileRead(divBtnLst.HttpFile, sLocalFileName[0] + "\\" + sLocalFileName[1], sServerFileName);
			break;

		case "cfmBtn" :
			//변경된 자료가 있는지 체크 => 변경된 자료가 있으면 작업 중지
            if ( fnc_DatasetChangeCheck(dsPcTax) ) {
                fnc_Message(SNIS_COM_1052, "N");
                return;
            }

            if( dsPcTax.rowcount == 0 ) {
                fnc_Message(SNIS_COM_1053, "N");
                return;
            }

            if(fnc_Message(SNIS_RBM_D003, "Y")) {           
                var sServiceName = "rsm5010-service:saveCfmCntnt";
                var sInDataSet   = "dsPcTax=dsPcTax:a ";
                var sOutDataSet  = "dsPcTax=dsPcTax ";    

                //서버로 넘길값
				fcd_AddParam("SEND_VALUE", "RSM5030");

                fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", false);
            }
            break;
	}
}

function fcDataCheck() {
	if( dsTemp.rowcount == 0 ) return;

	dsPcTax.ClearData();
	var sPayDtChk;

	for(var i = 0; i < dsTemp.rowcount ; i++) {

		var sCheck     = "";

		var sMeetCd    = dsTemp.GetColumn(i, "MEET_CD");
		var sSellCd    = dsTemp.GetColumn(i, "SELL_CD");
		var sTsn       = dsTemp.GetColumn(i, "TSN");
		var sCustSsn   = dsTemp.GetColumn(i, "CUST_SSN");
		var sCustNm    = dsTemp.GetColumn(i, "CUST_NM");
		var sCustAddr  = dsTemp.GetColumn(i, "CUST_ADDR");
		var sJigupDt   = dsTemp.GetColumn(i, "JIGEUP_DT");
		var sJigupAmt  = dsTemp.GetColumn(i, "JIGEUP_AMT");
		var sCost      = dsTemp.GetColumn(i, "COST");
		var sGita1     = dsTemp.GetColumn(i, "GITA1");
		var sGita2     = dsTemp.GetColumn(i, "GITA2");
		var sGitaPay   = dsTemp.GetColumn(i, "GITA_PAY");
		//var sRefunm    = dsTemp.GetColumn(i, "REFUND");
		//var sSellAmt   = dsTemp.GetColumn(i, "SELL_AMT");	
		var sForeNo    = dsTemp.GetColumn(i, "FORE_NO");
		var sPayYear   = substr(sJigupDt, 0, 4);

		if( sMeetCd == "1" || sMeetCd == "2" || sMeetCd == "3" || sMeetCd = "4" ) {
			sMeetCd = "00" + sMeetCd;
		} else {
			sMeetCd = "";
			sCheck  = "*";
		}

		if( sSellCd == "2" || sSellCd = "4" ) {
			sSellCd = "0" + sSellCd;
		} else {
			sSellCd = "";
			sCheck  = "*";
		}

		if( sTsn.length != 14) {
			sTsn = "";
			sCheck  = "*";
		}

		if( !fnc_ResidenceNumberCheck(sCustSsn) ) {
			sCustSsn = "";
			sCheck  = "*";
		} else {
			sCustSsn = substr(sCustSsn, 0, 6) + "-" + substr(sCustSsn, 6, 13);
		}

		if( !fnc_IsDate(sJigupDt) ) {
			sJigupDt = "";
			sCheck  = "*";
		}

		if( !IsDigit(sJigupAmt) ) {
			sJigupAmt = "";
			sCheck  = "*";
		}

		if( !IsDigit(sCost) ) {
			sCost = "";
			sCheck  = "*";
		}

		if( !IsDigit(sGita1) ) {
			sGita1 = "";
			sCheck  = "*";
		}

		if( !IsDigit(sGita2) ) {
			sGita2 = "";
			sCheck  = "*";
		}

		if( !IsDigit(sGitaPay) ) {
			sGitaPay = "";
			sCheck  = "*";
		}
		/*
		if( !IsDigit(sRefunm) ) {
			sRefunm = "";
			sCheck  = "*";
		}

		if( !IsDigit(sSellAmt) ) {
			sSellAmt = "";
			sCheck  = "*";
		}*/

		if (cboPayYear.Value != sPayYear) {
			sJigupDt = "";
			sCheck  = "*";
		}

		var nRow = dsPcTax.AddRow();

		dsPcTax.SetColumn(nRow, "MEET_CD"   , sMeetCd);
		dsPcTax.SetColumn(nRow, "SELL_CD"   , sSellCd);
		dsPcTax.SetColumn(nRow, "TSN"       , sTsn);
		dsPcTax.SetColumn(nRow, "CUST_SSN"  , sCustSsn);
		dsPcTax.SetColumn(nRow, "CUST_NM"   , sCustNm);
		dsPcTax.SetColumn(nRow, "CUST_ADDR" , sCustAddr);
		dsPcTax.SetColumn(nRow, "JIGEUP_DT" , sJigupDt);		
		dsPcTax.SetColumn(nRow, "PAY_YEAR"  , sPayYear);
		dsPcTax.SetColumn(nRow, "JIGEUP_AMT", sJigupAmt);
		dsPcTax.SetColumn(nRow, "COST"      , sCost);
		dsPcTax.SetColumn(nRow, "GITA1"     , sGita1);
		dsPcTax.SetColumn(nRow, "GITA2"     , sGita2);
		dsPcTax.SetColumn(nRow, "GITA_PAY"  , sGitaPay);
		dsPcTax.SetColumn(nRow, "REFUND"    , sRefunm);
		dsPcTax.SetColumn(nRow, "SELL_AMT"  , sSellAmt);		
		dsPcTax.SetColumn(nRow, "FORE_NO"   , sForeNo);
		dsPcTax.SetColumn(nRow, "ECHK"      , sCheck);		
	}
}
// 일괄확정취소
function Button0_OnClick(obj)
{	

	if( !fnc_Message(SNIS_CRA_B999, "Y","확정취소를 하시겠습니까?")) return; 

	var sServiceName = "rsm5010-service:saveCfmCancel";
	var sInDataSet   = "dsPcTax=dsPcTax ";
	var sOutDataSet  = "";    
	fcd_AddParam("SEND_VALUE"	, "FST");
	fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", false);
}
