function fcFormLoadSetting(obj) {  
    sBUTTONLIST   = 'FTFFTFT';

    //È­¸éÈ¯°æ ¼³Á¤
    sFORMCAPTION  = obj.Title;
    sFORMLOCATION = "";

	// È­¸é±×¸®µå ¸®»çÀÌÁî
	fcSetResize(this,this.width-4);	

    //È­¸é Á¤º¸ Display ¹× Button ±ÇÇÑ ¼³Á¤
    fnc_FormLoadSetting(obj, sFORMCAPTION, sFORMLOCATION, sBUTTONLIST, sUSERAUTH);

    //°øÅëÄÚµå Á¶È¸
    fcd_GetComboDs(dsInComCd);

	//Á¶È¸ Á¶°ÇÀÇ ½ÃÇàÃ³¿¡´Â 'ÀüÃ¼'¸¦ Ãß°¡ÇÑ´Ù.
    if( dsMeetCdSearch.rowcount > 0 ) {
		dsMeetCdSearch.InsertRow(0);
		dsMeetCdSearch.SetColumn(0, "CD_NM", "ÀüÃ¼");
		cboMeetCdSearch.index = 0;
	}

	//Á¶È¸ Á¶°ÇÀÇ ÆÇ¸ÅÃ³¿¡´Â 'ÀüÃ¼'¸¦ Ãß°¡ÇÑ´Ù.
    if( dsSellCdSearch.rowcount > 0 ) {
		dsSellCdSearch.InsertRow(0);
		dsSellCdSearch.SetColumn(0, "CD_NM", "ÀüÃ¼");
		cboSellCdSearch.index = 0;
	}

    fcSearch(); //½ÃÀÛ ½Ã Á¶È¸
}

