>>  Transaction ÈÄ Ã³¸® ÇØ¾ß ÇÒ ³»¿ë!
+-------------------------------------------------------------------------------------------------*/
function fcCallBack(sServiceName, ErrorCode, ErrorMSG) {
    if ( !fnc_result(ErrorCode, ErrorMSG) ) return;

    // Á¶È¸ ¿Ï·á ÈÄ
    if ( sServiceName == "rsm5060-service:searchPcGita" ) {
		stCntChkValid.Text = "(ÀÏÄ¡ "+dsCntChkValid.GetColumn(0,"CNT_CHK_VALID_T")+"°Ç, ºÒÀÏÄ¡ "+dsCntChkValid.GetColumn(0,"CNT_CHK_VALID_F")+"°Ç)";
        fcBtnControl();
    }

	// DBÀÔ·Â
    if ( sServiceName ==  "rsm5060-service:saveDbGitaFile") {
        var arr = split2(GBL_RET_VALUE,"^","=");
        if( arr[0][1] != "O"){
			fnc_Message(SNIS_CRA_B999, "N","DBÀÔ·ÂÀÌ ¿Ï·áµÇ¾ú½À´Ï´Ù!");	
			fcSearch();	
		}	
    }
    // È®Á¤
    if ( sServiceName == "rsm5060-service:saveGitaCfmCntnt" ) {
		var arr = split2(GBL_RET_VALUE,"^","=");

		GBL_RET_VALUE = "";	// »ç¿ëÇÑ GBL_RET_VALUE º¯¼ö´Â ÃÊ±âÈ­ ½ÃÄÑÁÜ ´Ù¸¥ ÆäÀÌÁö ÀÌµ¿ ÇÒ ¶§ µé°í°¨

		var RESULT	= fnc_Array2Value(arr,"RESULT"); // °á°ú

//        if( arr[0][1] != "O"){
		if( RESULT == "T"){
			fnc_Message(SNIS_CRA_B999, "N","È®Á¤ÀÌ ¿Ï·áµÇ¾ú½À´Ï´Ù!");
			fcSearch();
		}	
    }
    // È®Á¤Ãë¼Ò
    if ( sServiceName == "rsm5060-service:deleteGitaCfmCntnt" ) {
        fnc_Message(SNIS_CRA_B999, "N","È®Á¤Ãë¼Ò°¡ ¿Ï·áµÇ¾ú½À´Ï´Ù!");
		fcSearch();
    }

}

