/*-------------------------------------------------------------------------------------------------+
>>  조회 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcSearch(obj) {

	var strracedt = caltoDate.Value;	// 일자
	var strWinno  = edt_Winno.Text; 	// 창구번호
	var strTsnno  = edt_newTsn.Text; 	// tsn번호

	if( (fnc_IsNull(strWinno)) && ( fnc_IsNull(strTsnno)) ){	
		fnc_Message(SNIS_CRA_B999, "N","창구번호 또는 TSN은 필수 입력 항목 입니다.");
		return;
	}

	var sServiceName = "";
	if( !fnc_IsNull(strTsnno) && fnc_IsNull(strWinno)  ){
		sServiceName = "rsm6010-service:searchTsn";
		fcd_AddParam("tsn"		, strTsnno);
	}else{

		if( fnc_IsNull(strracedt) ){			
			fnc_Message(SNIS_COM_1035, "N","검색일자"); // 검색일자는 필수 입력 항목 입니다.
			caltodate.SetFocus();
			return;
		}
		if( !fnc_IsDate(strracedt) ){			
			fnc_Message(SNIS_CRA_F007, "N"); // "정확한 일자를 입력하십시요."
			caltodate.SetFocus();
			return;
		}
		sServiceName = "rsm6010-service:searchWin";				
		fcd_AddParam("race_day"	, caltodate.Value);
		fcd_AddParam("win_no"	,"00"+edt_winno.Text);
		fcd_AddParam("tsn"		, edt_newtsn.Text);

	}

	stCont.Text = "";
    var sInDataSet   = "";
    var sOutDataSet  = "dsTsnResult=dsTsnResult";
	fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", true);
}

