/*-------------------------------------------------------------------------------------------------+
>>  그리드에서 팝업 처리 
+-------------------------------------------------------------------------------------------------*/
function fcGrdOnExpandEdit(objGrd, nRow, nCell, strVal, nPivotIndex){
	switch ( objGrd.id ) {
		case "grd_result" :
			if (nCell == 12) {
				var deviceInfo = split(GetDeviceInfo(), ";");
				var screen = split(deviceInfo[2], "x");
				var iXPos = (parseInt(screen[0]) - parseInt(270))/2;
				var iYPos = (parseInt(screen[1]) - parseInt(210))/2;
				pdivCont2.TrackPopup(iXPos,iYPos);
			}
		break;
	}
}

