// TSN의 첫짜리 구하기
function btb_tsn_OnClick(obj)
{	

	var strBeforTsn = edt_beforTsn.Text;
	if( fnc_IsNull(strBeforTsn) ){		
		fnc_Message(SNIS_CRA_B999, "N","TSN을 입력하십시요!");
		edt_beforTsn.SetFocus();
		return;
	}

	var checksum = 0;
	var digit 	 = 0;
	var tsn_org  = strBeforTsn;
	var tsn 	 = replace(replace(tsn_org,"-")," ","");	

	if ( fnc_IsNull(tsn) || length(tsn)!= 12) {		
		fnc_Message(SNIS_CRA_B999, "N","TSN이 정확한지 다시 확인하세요.");
		edt_beforTsn.SetFocus();
		return;
	}

	for(i=0; i<length(tsn);i++) {
		digit = 0;			
		cUnit = charAt(tsn,i);
		if( (cUnit >= "0") && (cUnit <= "9") ) {
			digit = asc(cUnit) - 48; 
		}else if ((cUnit >= 'A') && (cUnit <= 'F')) {
			digit = asc(cUnit) - 65 + 10; 
		}else if ((cUnit >= 'a') && (cUnit <= 'f')) { 
			digit = asc(cUnit) - 97 + 10; 
		}
		checksum = checksum + (digit * (i+1));
	}

	checksum = checksum%16;
	if (checksum >= 10) { 
		checksum = checksum + 65 - 10 ; 
	} else { 
		checksum = checksum + 48; 
	}	
	edt_afterTsn.Text = chr(checksum) + "-" + tsn_org;
	edt_newTsn.Text   = chr(checksum) + "-" + tsn_org;

	calToDate.SetFocus();
}

function grd_result_OnMouseOver(obj,nPosX,nPosY,nRow,nCell,nPivotIndex)
{
	if( nCell == 13){
		obj.ToolTipText = dsTsnResult.GetColumn(nRow,"CONT2");
	}else{
		obj.ToolTipText = "";
	}
}

