/*-------------------------------------------------------------------------------------------------+
>>  Á¶È¸ ¹öÆ° Å¬¸¯ ãÁ Ã³¸®
+-------------------------------------------------------------------------------------------------*/
function fcSearch(obj) {

	var strracedt = caltoDate.Value;	// ÀÏÀÚ
	var strWinno  = edt_Winno.Text; 	// Ã¢±¸¹øÈ£
	var strTsnno  = edt_newTsn.Text; 	// tsn¹øÈ£

	if( (fnc_IsNull(strWinno)) && ( fnc_IsNull(strTsnno)) ){	
		fnc_Message(SNIS_CRA_B999, "N","Ã¢±¸¹øÈ£ ¶Ç´Â TSNÀº ÇÊ¼ö ÀÔ·Â Ç×¸ñ ÀÔ´Ï´Ù.");
		return;
	}

	var sServiceName = "";
	if( !fnc_IsNull(strTsnno) && fnc_IsNull(strWinno)  ){
		sServiceName = "rsm6010-service:searchTsn";
		fcd_AddParam("tsn"		, strTsnno);
	}else{

		if( fnc_IsNull(strracedt) ){			
			fnc_Message(SNIS_COM_1035, "N","°Ë»öÀÏÀÚ"); // °Ë»öÀÏÀÚ´Â ÇÊ¼ö ÀÔ·Â Ç×¸ñ ÀÔ´Ï´Ù.
			caltodate.SetFocus();
			return;
		}
		if( !fnc_IsDate(strracedt) ){			
			fnc_Message(SNIS_CRA_F007, "N"); // "Á¤È®ÇÑ ÀÏÀÚ¸¦ ÀÔ·ÂÇÏ½Ê½Ã¿ä."
			caltodate.SetFocus();
			return;
		}
		sServiceName = "rsm6010-service:searchWin";				
		fcd_AddParam("race_day"	, caltodate.Value);
		fcd_AddParam("win_no"	,"00"+edt_winno.Text);
		fcd_AddParam("tsn"		, edt_newtsn.Text);

	}

	stCont.Text = "";
    var sInDataSet   = "";
    var sOutDataSet  = "dsTsnResult=dsTsnResult";
	fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", true);
}

