/*-------------------------------------------------------------------------------------------------+
>>  최초 화면 Load時 처리 할 사항
+-------------------------------------------------------------------------------------------------*/
function fcFormLoadSetting(obj) {  
    //sBUTTONLIST   = 'F'+sIsView+sInpt+'TTFT';     //초기화,조회,저장,삭제,엑셀,인쇄,닫기
    sBUTTONLIST   = 'F'+sIsView+'FTTFT';

 	//화면환경 설정
	sFORMCAPTION  = obj.Title;
	sFORMLOCATION = "";

	//화면 정보 Display 및 Button 권한 설정
	fnc_FormLoadSetting(obj, sFORMCAPTION, sFORMLOCATION, sBUTTONLIST, sUSERAUTH);

	//초기 화면 BK Color설정 
	fnc_SetBKColor(this);

    //공통코드 조회
	fcd_GetComboDs(dsInComCd);

	dsBrncCd.InsertRow(0);
	dsBrncCd.SetColumn(0, "CD_NM", "전체");
	tabGbn.Tab1.cbBrncCd.index = 0;
	tabGbn.Tab2.cbbrnccd.index = 0;


	// 현재일기준으로 세팅한다.
	tabGbn.tab1.calFromDate.Value = addDate(today(),0);
	tabGbn.tab1.caltoDate.Value = addDate(today(),0);

	tabGbn.tab2.calFromDate.Value = addDate(today(),0);
	tabGbn.tab2.caltoDate.Value = addDate(today(),0);

}

/*-------------------------------------------------------------------------------------------------+
>>  최초 화면 UNLoad時 처리 할 사항
+-------------------------------------------------------------------------------------------------*/
function fcFormUnloadProcess(obj) {
	//자료 변경 여부를 체크하고자 하는  DataSet Object를 넘긴다.
	var sDataset = "dsTsnResult|dsTsnResultDas";
	if (   !fnc_FormUnloadCheck(sDataset  , sFormId) ) return false;
}

/*-------------------------------------------------------------------------------------------------+
>>  단축키 처리(Form KeyDown)
+-------------------------------------------------------------------------------------------------*/
function fcHotKey(obj,objSenderObj,nChar,bShift,bControl,bAlt,nLLParam,nHLParam) {

	//Return Key Press時 Next Component focus
	if ( nChar == 13 ) {
		var oFocusComponent = GetNextComponent(true);
		oFocusComponent.setfocus();
		return;
	}
}

