/*-------------------------------------------------------------------------------------------------+
>>  조회 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcSearch(obj) {

	var sServiceName = "";
    var sInDataSet   = "";
    var sOutDataSet  = "";
	var strBrncCd	 = "";
    if( tabGbn.TabIndex == 0){

		if( tabGbn.tab1.cbBrncCd.Value == "00"){
			strBrncCd = "^(0[012348])$";
		}else{
			strBrncCd = tabGbn.tab1.cbBrncCd.Value;

		}


		// 지불정지만 존재 조회시.
		sServiceName = "rsm6030-service:searchTsn";
		sInDataSet   = "";
		sOutDataSet  = "dsTsnResult=dsTsnResult ";

		fcd_AddParam("tsnno"	, tabGbn.tab1.edtTsn.Text ); 				// TSN
//		fcd_AddParam("brnccd"	, decode(strBrncCd,'00','',strBrncCd) ); 	// 본장/지점
		fcd_AddParam("brnccd"	, strBrncCd); 	// 본장/지점
		fcd_AddParam("fromdate" , tabGbn.tab1.calFromDate.Value ); 			// 검증 시작일
		fcd_AddParam("todate"	, tabGbn.tab1.calToDate.Value ); 			// 검증 종료일

    }else{

		if( tabGbn.tab2.cbBrncCd.Value == "00"){
			strBrncCd = "^(0[012348])$";
		}else{
			strBrncCd = tabGbn.tab2.cbBrncCd.Value;

		}


		// DAS와 지불정지 존재
		sServiceName = "rsm6030-service:searchTsnDas";
		sInDataSet   = "";
		sOutDataSet  = "dsTsnResultDas=dsTsnResultDas ";

		fcd_AddParam("vericd"	, tabGbn.tab2.cbVeriCd.Value ); 			// 검증 조치 
		fcd_AddParam("tsnno"	, tabGbn.tab2.edtTsn.Text ); 				// TSN
//		fcd_AddParam("brnccd"	, decode(strBrncCd,'00','',strBrncCd) ); 	// 본장/지점		
		fcd_AddParam("brnccd"	, strBrncCd ); 	// 본장/지점		
		fcd_AddParam("fromdate"	, tabGbn.tab2.calFromDate.Value ); 			// 검증 시작일
		fcd_AddParam("todate"	, tabGbn.tab2.calToDate.Value ); 			// 검증 종료일

    }

	fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", false);

}

